import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { testApi } from "../../apis/Api";

const Homepage = () => {
    const [interval, setInterval] = useState(2000); // Initial interval set to 2000ms (2 seconds)

    // Print hello! when page loads (Automatic)
    useEffect(() => {
        console.log("hello world !!");
        // Trigger testApi
        testApi().then((res) => {
            console.log(res);  // "test api is working"
        });
    }, []);  // Note the empty dependency array to ensure this runs only once when the component mounts

    // Function to handle the interval change
    const handleIntervalChange = (e) => {
        const newInterval = e.target.value * 1000; // Convert seconds to milliseconds
        setInterval(newInterval);
        // Update the carousel interval
        document.querySelector('#carouselExampleAutoplaying').setAttribute('data-bs-interval', newInterval);
    };

    return (
        <>
            <Navbar />

            <div className="container mt-4">
                <div className="mb-3">
                    <label htmlFor="carouselSpeed" className="form-label">Carousel Speed (seconds):</label>
                    <input 
                        type="number" 
                        className="form-control" 
                        id="carouselSpeed" 
                        value={interval / 1000} 
                        onChange={handleIntervalChange} 
                        min="1" 
                        step="1" 
                    />
                </div>

                <div id="carouselExampleAutoplaying" className="carousel slide" data-bs-ride="carousel" data-bs-interval={interval}>
                    <div className="carousel-inner">
                        <div className="carousel-item active">
                            <img 
                                src="https://kidlingoo.com/wp-content/uploads/flowers_name_in_english.jpg" 
                                className="d-block w-100" 
                                alt="..." 
                                style={{ width: '800px', height: '400px' }} // Adjust the size here
                            />
                        </div>
                        <div className="carousel-item">
                            <img 
                                src="https://i.pinimg.com/736x/90/40/03/9040034f5d635f46a4fb92128964fcca.jpg" 
                                className="d-block w-100" 
                                alt="..." 
                                style={{ width: '800px', height: '400px' }} // Adjust the size here
                            />
                        </div>
                        <div className="carousel-item">
                            <img 
                                src="https://hips.hearstapps.com/hmg-prod/images/hummingbird-feeding-at-bleeding-heart-bloom-royalty-free-image-1656108706.jpg?crop=0.536xw:1.00xh;0.116xw,0&resize=980:*" 
                                className="d-block w-100" 
                                alt="..." 
                                style={{ width: '800px', height: '400px' }} // Adjust the size here
                            />
                        </div>
                    </div>
                    <button className="carousel-control-prev" type="button" data-bs-target="#carouselExampleAutoplaying" data-bs-slide="prev">
                        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Previous</span>
                    </button>
                    <button className="carousel-control-next" type="button" data-bs-target="#carouselExampleAutoplaying" data-bs-slide="next">
                        <span className="carousel-control-next-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Next</span>
                    </button>
                </div>
            </div>
        </>
    );
}

export default Homepage;
