import axios from 'axios';
import { toast } from 'react-toastify';

// Configuration Constants
const API_CONFIG = {
    BASE_URL: process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api',
    TIMEOUT: parseInt(process.env.REACT_APP_API_TIMEOUT) || 30000,
    SESSION_TIMEOUT: parseInt(process.env.REACT_APP_SESSION_TIMEOUT) || 60 * 1000,
    WARNING_TIME: parseInt(process.env.REACT_APP_WARNING_TIME) || 45 * 1000,
    CHECK_INTERVAL: parseInt(process.env.REACT_APP_CHECK_INTERVAL) || 10000
};

// Storage Keys
const STORAGE_KEYS = {
    TOKEN: 'token',
    USER_ID: 'userId',
    USER: 'user',
    REFRESH_TOKEN: 'refreshToken',
    LAST_ACTIVE: 'lastActive',
    SESSION_ID: 'sessionId',
    SESSION_START: 'sessionStart',
    SESSION_WARNING_SHOWN: 'sessionWarningShown',
    REMEMBER_ME: 'rememberMe'
};

// Utility Functions
const getCurrentDateTime = () => {
    const now = new Date();
    return now.toISOString().slice(0, 19).replace('T', ' ');
};

const getSystemInfo = () => {
    return {
        userAgent: navigator.userAgent,
        platform: navigator.platform,
        language: navigator.language,
        timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
        screenResolution: `${window.screen.width}x${window.screen.height}`,
        timestamp: getCurrentDateTime()
    };
};

const clearStorage = () => {
    Object.values(STORAGE_KEYS).forEach(key => {
        try {
            localStorage.removeItem(key);
            sessionStorage.removeItem(key);
        } catch (error) {
            console.error(`Error clearing ${key}:`, error);
        }
    });
    
    document.cookie.split(";").forEach(cookie => {
        document.cookie = cookie
            .replace(/^ +/, "")
            .replace(/=.*/, `=;expires=${new Date().toUTCString()};path=/`);
    });
};

const updateStoredUserData = (userData) => {
    if (!userData) return;
    
    const currentDateTime = getCurrentDateTime();
    const storedUser = {
        ...userData,
        lastActive: currentDateTime,
        lastUpdated: currentDateTime,
        systemInfo: getSystemInfo()
    };

    try {
        localStorage.setItem(STORAGE_KEYS.USER, JSON.stringify(storedUser));
        localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, currentDateTime);
    } catch (error) {
        console.error('Error updating user data:', error);
        throw new Error('Failed to update user data');
    }
};

const checkSessionExpiry = () => {
    const lastActive = localStorage.getItem(STORAGE_KEYS.LAST_ACTIVE);
    if (!lastActive) return true;

    const currentTime = new Date().getTime();
    const lastActiveTime = new Date(lastActive).getTime();
    const timeDifference = currentTime - lastActiveTime;

    if (timeDifference > API_CONFIG.WARNING_TIME && 
        !localStorage.getItem(STORAGE_KEYS.SESSION_WARNING_SHOWN)) {
        toast.warning('Your session will expire soon. Please save your work.');
        localStorage.setItem(STORAGE_KEYS.SESSION_WARNING_SHOWN, 'true');
    }

    return timeDifference > API_CONFIG.SESSION_TIMEOUT;
};

// API Instance
const Api = axios.create({
    baseURL: API_CONFIG.BASE_URL,
    timeout: API_CONFIG.TIMEOUT,
    withCredentials: true,
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    }
});

// Request Interceptor
Api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem(STORAGE_KEYS.TOKEN);
        const currentDateTime = getCurrentDateTime();
        
        if (checkSessionExpiry()) {
            clearStorage();
            window.location.href = '/login?session=expired';
            return Promise.reject('Session expired');
        }

        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }

        config.metadata = {
            startTime: new Date(),
            requestId: Math.random().toString(36).substring(7),
            timestamp: currentDateTime,
            systemInfo: getSystemInfo()
        };

        localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, currentDateTime);

        return config;
    },
    (error) => Promise.reject(handleApiError(error))
);

// Response Interceptor
Api.interceptors.response.use(
    (response) => {
        const currentDateTime = getCurrentDateTime();
        localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, currentDateTime);
        
        return {
            ...response,
            timestamp: currentDateTime,
            duration: new Date() - response.config.metadata.startTime
        };
    },
    (error) => {
        if (error.response?.status === 401) {
            clearStorage();
            window.location.href = '/login?session=expired';
        }
        return Promise.reject(handleApiError(error));
    }
);

// Error Handler
const handleApiError = (error) => {
    const errorMessage = error.response?.data?.message || error.message || 'An unexpected error occurred';
    const currentDateTime = getCurrentDateTime();
    
    console.error('API Error:', {
        message: errorMessage,
        timestamp: currentDateTime,
        error: error
    });

    toast.error(errorMessage);
    return {
        message: errorMessage,
        timestamp: currentDateTime,
        status: error.response?.status,
        data: error.response?.data
    };
};

// API Functions
export const isLoggedIn = () => {
    try {
        const token = localStorage.getItem(STORAGE_KEYS.TOKEN);
        const userId = localStorage.getItem(STORAGE_KEYS.USER_ID);
        const lastActive = localStorage.getItem(STORAGE_KEYS.LAST_ACTIVE);

        if (!token || !userId) return false;

        if (lastActive) {
            const lastActiveTime = new Date(lastActive).getTime();
            const currentTime = new Date().getTime();
            if (currentTime - lastActiveTime > API_CONFIG.SESSION_TIMEOUT) {
                clearStorage();
                return false;
            }
        }

        return true;
    } catch (error) {
        console.error('Auth check error:', error);
        return false;
    }
};

export const getStoredUserData = () => {
    try {
        const userData = localStorage.getItem(STORAGE_KEYS.USER);
        return userData ? JSON.parse(userData) : null;
    } catch (error) {
        console.error('Error getting user data:', error);
        return null;
    }
};

export const loginApi = async (data) => {
    try {
        clearStorage();
        const currentDateTime = getCurrentDateTime();
        
        const loginData = {
            ...data,
            timestamp: currentDateTime,
            systemInfo: getSystemInfo()
        };

        const response = await Api.post('/users/login', loginData);

        if (response.data?.data) {
            const { user, token } = response.data.data;
            
            localStorage.setItem(STORAGE_KEYS.TOKEN, token);
            localStorage.setItem(STORAGE_KEYS.USER_ID, user._id);
            localStorage.setItem(STORAGE_KEYS.SESSION_START, currentDateTime);
            localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, currentDateTime);
            
            updateStoredUserData(user);
            toast.success(`Welcome back, ${user.nickname}!`);
        }
        
        return response;
    } catch (error) {
        clearStorage();
        throw handleApiError(error);
    }
};

export const signupApi = async (data) => {
    try {
        const response = await Api.post('/users/signup', {
            ...data,
            timestamp: getCurrentDateTime(),
            systemInfo: getSystemInfo()
        });
        toast.success('Registration successful! Please log in.');
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const createConfession = async (confessionData) => {
    try {
        const formData = new FormData();
        const currentDateTime = getCurrentDateTime();
        
        const currentData = {
            ...confessionData,
            timestamp: currentDateTime
        };

        Object.keys(currentData).forEach(key => {
            if (key !== 'image') {
                formData.append(key, currentData[key]);
            }
        });
        
        if (confessionData.image) {
            formData.append('image', confessionData.image);
        }

        const response = await Api.post('users/confessions/create', formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
        });

        if (response.data?.success) {
            toast.success('Confession created successfully!');
        }

        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const updateConfession = async (id, confessionData) => {
    try {
        const formData = new FormData();
        const currentDateTime = getCurrentDateTime();
        
        const currentData = {
            ...confessionData,
            updatedAt: currentDateTime
        };

        Object.keys(currentData).forEach(key => {
            if (key !== 'image') {
                formData.append(key, currentData[key]);
            }
        });
        
        if (confessionData.image) {
            formData.append('image', confessionData.image);
        }

        const response = await Api.put(`users/confessions/${id}`, formData, {
            headers: { 'Content-Type': 'multipart/form-data' }
        });

        if (response.data?.success) {
            toast.success('Confession updated successfully!');
        }

        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const deleteConfession = async (id) => {
    try {
        const response = await Api.delete(`users/confessions/${id}`);
        if (response.data?.success) {
            toast.success('Confession deleted successfully!');
        }
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const getUserConfessions = async () => {
    try {
        return await Api.get('users/confessions/my');
    } catch (error) {
        throw handleApiError(error);
    }
};

export const getUserProfile = async () => {
    try {
        const userId = localStorage.getItem(STORAGE_KEYS.USER_ID);
        if (!userId) throw new Error('User ID not found');

        const response = await Api.get(`/users/profile/${userId}`);
        if (response.data?.data) {
            updateStoredUserData(response.data.data);
        }
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const updateUserProfile = async (userData) => {
    try {
        const response = await Api.put('/users/profile/update', {
            ...userData,
            timestamp: getCurrentDateTime()
        });
        
        if (response.data?.success) {
            updateStoredUserData(response.data.user);
            toast.success('Profile updated successfully!');
        }
        
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const changePassword = async (passwordData) => {
    try {
        const response = await Api.put('/users/change-password', {
            ...passwordData,
            timestamp: getCurrentDateTime()
        });
        
        if (response.data?.success) {
            toast.success('Password changed successfully!');
        }
        
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const forgotPasswordApi = async (email) => {
    try {
        const response = await Api.post('/users/forgot-password', {
            email,
            timestamp: getCurrentDateTime()
        });
        toast.success('Password reset link sent!');
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const resetPasswordApi = async (token, newPassword) => {
    try {
        const response = await Api.post(`/users/reset-password/${token}`, {
            newPassword,
            timestamp: getCurrentDateTime()
        });
        toast.success('Password reset successful!');
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const submitContactUs = async (contactData) => {
    try {
        const response = await Api.post('/users/contactUs', {
            ...contactData,
            timestamp: getCurrentDateTime()
        });
        
        if (response.data?.success) {
            toast.success('Message sent successfully!');
        }
        
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

// Initialize session checker
