// Navbar.jsx
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = ({ isAuthenticated, onLogout }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [searchFocused, setSearchFocused] = useState(false);
  const [currentDateTime, setCurrentDateTime] = useState('');
  const [userData, setUserData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const updateDateTime = () => {
      const now = new Date();
      const formattedDateTime = now.toISOString().slice(0, 19).replace('T', ' ');
      setCurrentDateTime(formattedDateTime);
    };

    updateDateTime();
    const timer = setInterval(updateDateTime, 1000);

    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUserData(storedUser);
    }

    return () => clearInterval(timer);
  }, []);

  const handleLogout = () => {
    onLogout();
    navigate('/login');
    setIsOpen(false);
  };

  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="modern-navbar">
      <div className="navbar-container">
        <div className="navbar-left">
          <Link to="/" className="navbar-brand">
            Confess <span className="brand-highlight">here</span>
          </Link>
          {isAuthenticated && (
            <div className="user-info">
              <p className="current-datetime">
                Current Date and Time (UTC - YYYY-MM-DD HH:MM:SS formatted): {currentDateTime}
              </p>
              <p className="current-user">
                Current User's Login: {userData?.nickname}
              </p>
            </div>
          )}
        </div>

        <div className={`navbar-right ${isOpen ? 'show' : ''}`}>
          <button 
            className={`navbar-toggler ${isOpen ? 'active' : ''}`} 
            onClick={toggleNavbar}
            aria-label="Toggle navigation"
          >
            <span className="toggler-icon"></span>
            <span className="toggler-icon"></span>
            <span className="toggler-icon"></span>
          </button>

          <div className="navbar-collapse">
            <div className={`search-container ${searchFocused ? 'focused' : ''}`}>
              <input
                type="search"
                placeholder="Search..."
                onFocus={() => setSearchFocused(true)}
                onBlur={() => setSearchFocused(false)}
                className="search-input"
              />
              <button className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </div>

            <ul className="navbar-nav">
              <li className="nav-item">
                <Link to="/communities" className="nav-link" onClick={() => setIsOpen(false)}>
                  <i className="fas fa-users"></i>
                  Communities
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/contact" className="nav-link" onClick={() => setIsOpen(false)}>
                  <i className="fas fa-envelope"></i>
                  Contact us
                </Link>
              </li>
              {isAuthenticated && (
                <>
                  <li className="nav-item">
                    <Link to="/profile" className="nav-link" onClick={() => setIsOpen(false)}>
                      <i className="fas fa-user"></i>
                      Profile
                    </Link>
                  </li>
                  <li className="nav-item">
                    <button onClick={handleLogout} className="logout-button">
                      <i className="fas fa-sign-out-alt"></i>
                      Logout
                    </button>
                  </li>
                </>
              )}
            </ul>
            
            {!isAuthenticated && (
              <div className="auth-buttons">
                <Link to="/register" className="register-button" onClick={() => setIsOpen(false)}>
                  Register
                </Link>
                <Link to="/login" className="login-button" onClick={() => setIsOpen(false)}>
                  Login
                </Link>
              </div>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
