// src/pages/confession/CreateConfession.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './CreateConfession.css';

const CreateConfession = () => {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        category: 'Personal',
        image: null,
        isAnonymous: false
    });
    const [loading, setLoading] = useState(false);
    const [imagePreview, setImagePreview] = useState(null);

    // Constants
    const MAX_TITLE_LENGTH = 100;
    const MAX_CONTENT_LENGTH = 5000;
    const MAX_IMAGE_SIZE = 5 * 1024 * 1024; // 5MB
    const ALLOWED_IMAGE_TYPES = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];

    const categories = [
        'Personal',
        'Relationship',
        'Work',
        'Family',
        'School',
        'Other'
    ];

    // Check authentication and get user info
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login', { 
                state: { from: '/create-confession' }
            });
        }
    }, [navigate]);

    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (!file) return;

        // Validate file type
        if (!ALLOWED_IMAGE_TYPES.includes(file.type)) {
            toast.error('Please upload only JPEG, PNG, GIF, or WebP images');
            return;
        }

        // Validate file size
        if (file.size > MAX_IMAGE_SIZE) {
            toast.error('Image size should be less than 5MB');
            return;
        }

        setFormData(prev => ({ ...prev, image: file }));
        
        // Create image preview
        const reader = new FileReader();
        reader.onloadend = () => {
            setImagePreview(reader.result);
        };
        reader.readAsDataURL(file);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!formData.title.trim()) {
            toast.error('Please enter a title');
            return;
        }

        if (!formData.content.trim()) {
            toast.error('Please enter your confession');
            return;
        }

        setLoading(true);

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('title', formData.title.trim());
            formDataToSend.append('content', formData.content.trim());
            formDataToSend.append('category', formData.category);
            formDataToSend.append('isAnonymous', formData.isAnonymous);
            formDataToSend.append('timestamp', new Date().toISOString());
            
            if (formData.image) {
                formDataToSend.append('image', formData.image);
            }

            // API call will be implemented later
            toast.success('Confession created successfully!');
            navigate('/');
        } catch (error) {
            toast.error(error.message || 'Failed to create confession');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="create-confession-container">
            <div className="confession-form-wrapper">
                <div className="confession-header">
                    <h2>Share Your Confession</h2>
                    <p>Express yourself freely and securely</p>
                </div>
                
                <form onSubmit={handleSubmit} className="confession-form">
                    <div className="form-group">
                        <label htmlFor="title">
                            Title 
                            <span className={`character-count ${
                                formData.title.length > MAX_TITLE_LENGTH * 0.9 ? 'near-limit' : ''
                            }`}>
                                {formData.title.length}/{MAX_TITLE_LENGTH}
                            </span>
                        </label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={formData.title}
                            onChange={handleInputChange}
                            required
                            maxLength={MAX_TITLE_LENGTH}
                            placeholder="Give your confession a title"
                            className="form-input"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="content">
                            Your Confession
                            <span className={`character-count ${
                                formData.content.length > MAX_CONTENT_LENGTH * 0.9 ? 'near-limit' : ''
                            }`}>
                                {formData.content.length}/{MAX_CONTENT_LENGTH}
                            </span>
                        </label>
                        <textarea
                            id="content"
                            name="content"
                            value={formData.content}
                            onChange={handleInputChange}
                            required
                            maxLength={MAX_CONTENT_LENGTH}
                            placeholder="Share your thoughts..."
                            className="form-textarea"
                            rows="6"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="category">Select Category</label>
                        <select
                            id="category"
                            name="category"
                            value={formData.category}
                            onChange={handleInputChange}
                            required
                            className="form-select"
                        >
                            {categories.map(category => (
                                <option key={category} value={category}>
                                    {category}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group file-upload-group">
                        <label htmlFor="image">
                            Add Image (optional)
                            <span className="file-info">
                                Accepted formats: JPEG, PNG, GIF, WebP (Max: 5MB)
                            </span>
                        </label>
                        <input
                            type="file"
                            id="image"
                            name="image"
                            accept={ALLOWED_IMAGE_TYPES.join(',')}
                            onChange={handleImageChange}
                            className="form-input-file"
                        />
                        {imagePreview && (
                            <div className="image-preview">
                                <img src={imagePreview} alt="Preview" />
                                <button 
                                    type="button" 
                                    onClick={() => {
                                        setImagePreview(null);
                                        setFormData(prev => ({ ...prev, image: null }));
                                    }}
                                    className="remove-image-btn"
                                >
                                    Remove Image
                                </button>
                            </div>
                        )}
                    </div>

                    <div className="form-group checkbox-group">
                        <label className="checkbox-label">
                            <input
                                type="checkbox"
                                name="isAnonymous"
                                checked={formData.isAnonymous}
                                onChange={handleInputChange}
                                className="checkbox-input"
                            />
                            Post Anonymously
                            <span className="checkbox-text">Your identity will be hidden from other users</span>
                        </label>
                    </div>

                    <button 
                        type="submit" 
                        className={`submit-button ${loading ? 'loading' : ''}`}
                        disabled={loading}
                    >
                        {loading ? 'Creating...' : 'Share Confession'}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default CreateConfession;
