//rafce -----> type it ..........> react arrow function

import React, { useState } from 'react';
import { signupApi } from "../../apis/Api";

const Registerpage = () => {

  // useState

  //logic section



  // usestate use garera temporary value hold garna lai variable banako
  //make a useState for 5 fields

  const [nickname, setNickName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [retypepassword, setRetypePassword] = useState('')





  // Use State for Error Message

  const [nicknameError, setNickNameError] = useState('')
  const [emailError, setEmailError] = useState('')
  const [passwordError, setPasswordError] = useState('')
  const [retypepasswordError, setRetypePasswordError] = useState('')







  // OnChange

  // make a each function for changing the value

  //e for event such as typing in the text field, a default parameter of handleFirstname parameter

  const handleNickname = (e) => {
    setNickName(e.target.value)

  }

 

  const handleEmail = (e) => {
    setEmail(e.target.value)

  }


  const handlePassword = (e) => {
    setPassword(e.target.value)

  }


  const handleRetypepassword = (e) => {
    setRetypePassword(e.target.value)

  }


  // validation
  var validate = () => {

    var isValid = true;

    //validate the first name

    //.trim for removing forward and backward extra spacing
    if (nickname.trim() === '') {

      setNickNameError("Nickname is Required!")

      isValid = false
    }

   

    if (email.trim() === '') {

      setEmailError("Email is Required!")

      isValid = false
    }

    if (password.trim() === '') {

      setPasswordError("password is Required!")

      isValid = false
    }

    if (retypepassword.trim() === '') {

      setRetypePasswordError("Confirm password is required")

      isValid = false
    }

    if (retypepassword.trim() !== password.trim()) {

      setRetypePasswordError("password doesnot matched")

      isValid = false
    }

    return isValid;


  }


  //submit button function

  //preventDefault ----> preventing event from getting refreshed in zero second


  const handleSubmit = async (e) => {
    e.preventDefault();

  
};








  return (
    <>

      <div className='container mt-2'>
        <h1> Create an Account! </h1>



        <form className='w-50'>

          <label> NickName : {nickname} </label>
          <input onChange={handleNickname} type="text" className='form-control' placeholder='Enter your Nickname' />

          {
            nicknameError && <p className='text-danger'> {nicknameError} </p>
          }


          <label className='mt-2'>  Email </label>
          <input onChange={handleEmail} type="text" className='form-control' placeholder='Enter your Email' />
          {
            emailError && <p className='text-danger'> {emailError} </p>
          }

          <label className='mt-2'> Password</label>
          <input onChange={handlePassword} type="text" className='form-control' placeholder='Enter your Password' />
          {
            passwordError && <p className='text-danger'> {passwordError} </p>
          }

          <label className='mt-2'>  Re-type Password</label>
          <input onChange={handleRetypepassword} type="text" className='form-control' placeholder=' Confirm your password' />
          {
            retypepasswordError && <p className='text-danger'> {retypepasswordError} </p>
          }

          <button onClick={handleSubmit} className='btn btn-dark mt-2 w-100'>Create an Account</button>

        </form>

      </div>
    </>
  )
}

export default Registerpage;


