import React, { useEffect, useState } from 'react';
import { 
  getUserProfile, 
  updateUserProfile, 
  changePassword, 
  getUserConfessions,
  deleteConfession,
} from '../../apis/Api';
import { useNavigate } from 'react-router-dom';

const Profilepage = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [currentDateTime, setCurrentDateTime] = useState('');
  const [formData, setFormData] = useState({
    nickname: '',
    email: ''
  });

  const [showPasswordSection, setShowPasswordSection] = useState(false);
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [passwordError, setPasswordError] = useState('');
  const [passwordSuccess, setPasswordSuccess] = useState('');

  const [userConfessions, setUserConfessions] = useState([]);
  const [postsLoading, setPostsLoading] = useState(true);
  const [postsError, setPostsError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(5);
  const [sortBy, setSortBy] = useState('newest');
  const [filterCategory, setFilterCategory] = useState('all');
  const [selectedPost, setSelectedPost] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (!storedUser) {
          throw new Error('User data not found');
        }

        const [profileResponse, confessionsResponse] = await Promise.all([
          getUserProfile(),
          getUserConfessions()
        ]);

        if (profileResponse.data?.success && profileResponse.data?.data) {
          const profileData = profileResponse.data.data;
          setUserData(profileData);
          setFormData({
            nickname: profileData.nickname,
            email: profileData.email
          });
        }

        if (confessionsResponse.data?.success) {
          setUserConfessions(confessionsResponse.data.confessions);
        }
      } catch (err) {
        console.error('Data fetch error:', err);
        setError('Could not load profile data. Please try again.');
      } finally {
        setLoading(false);
        setPostsLoading(false);
      }
    };

    fetchUserProfile();

    const updateDateTime = () => {
      const now = new Date();
      const year = now.getUTCFullYear();
      const month = String(now.getUTCMonth() + 1).padStart(2, '0');
      const day = String(now.getUTCDate()).padStart(2, '0');
      const hours = String(now.getUTCHours()).padStart(2, '0');
      const minutes = String(now.getUTCMinutes()).padStart(2, '0');
      const seconds = String(now.getUTCSeconds()).padStart(2, '0');
      const formattedDateTime = `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
      setCurrentDateTime(formattedDateTime);
    };

    updateDateTime();
    const timer = setInterval(updateDateTime, 1000);

    return () => clearInterval(timer);
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleEdit = () => {
    setIsEditing(true);
    setError(null);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setFormData({
      nickname: userData.nickname,
      email: userData.email
    });
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);

      const response = await updateUserProfile(formData);
      
      if (response.data.success) {
        setUserData(response.data.user);
        setIsEditing(false);
      }
    } catch (err) {
      setError(err.message || 'Failed to update profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setPasswordError('');
    setPasswordSuccess('');

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setPasswordError('New passwords do not match');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      setPasswordError('New password must be at least 6 characters long');
      return;
    }

    try {
      setPasswordLoading(true);
      const response = await changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      if (response.data.success) {
        setPasswordSuccess('Password changed successfully');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          setShowPasswordSection(false);
          setPasswordSuccess('');
        }, 2000);
      }
    } catch (error) {
      setPasswordError(error.message || 'Failed to change password');
    } finally {
      setPasswordLoading(false);
    }
  };

  const handleDeleteConfession = async (confessionId) => {
    if (window.confirm('Are you sure you want to delete this confession?')) {
      try {
        await deleteConfession(confessionId);
        setUserConfessions(userConfessions.filter(conf => conf.id !== confessionId));
        if (selectedPost?.id === confessionId) {
          setSelectedPost(null);
        }
      } catch (err) {
        console.error('Failed to delete confession:', err);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    const year = date.getUTCFullYear();
    const month = String(date.getUTCMonth() + 1).padStart(2, '0');
    const day = String(date.getUTCDate()).padStart(2, '0');
    const hours = String(date.getUTCHours()).padStart(2, '0');
    const minutes = String(date.getUTCMinutes()).padStart(2, '0');
    const seconds = String(date.getUTCSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  };

  const getSortedAndFilteredConfessions = () => {
    let filtered = [...userConfessions];

    if (filterCategory !== 'all') {
      filtered = filtered.filter(conf => conf.category === filterCategory);
    }

    switch (sortBy) {
      case 'newest':
        return filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      case 'oldest':
        return filtered.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
      case 'mostLiked':
        return filtered.sort((a, b) => b.likes - a.likes);
      case 'mostCommented':
        return filtered.sort((a, b) => b.comments - a.comments);
      default:
        return filtered;
    }
  };

  const indexOfLastConfession = currentPage * postsPerPage;
  const indexOfFirstConfession = indexOfLastConfession - postsPerPage;
  const currentConfessions = getSortedAndFilteredConfessions().slice(
    indexOfFirstConfession,
    indexOfLastConfession
  );
  const totalPages = Math.ceil(getSortedAndFilteredConfessions().length / postsPerPage);

  if (loading && !userData) {
    return (
      <div className="profile-page">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-layout">
        <div className="profile-info-header">
          <div className="datetime-display">
            Current Date and Time (UTC - YYYY-MM-DD HH:MM:SS formatted): {currentDateTime}
          </div>
          <div className="user-display">
            Current User's Login: {userData?.nickname || '2025raj'}
          </div>
        </div>

        <div className="main-content">
          {/* Left Section - Confessions */}
          <div className="confessions-section">
            <div className="section-header">
              <h2>Your Confessions</h2>
              <div className="controls">
                <select 
                  value={sortBy} 
                  onChange={(e) => setSortBy(e.target.value)}
                  className="sort-select"
                >
                  <option value="newest">Newest First</option>
                  <option value="oldest">Oldest First</option>
                  <option value="mostLiked">Most Liked</option>
                  <option value="mostCommented">Most Commented</option>
                </select>

                <select 
                  value={filterCategory} 
                  onChange={(e) => setFilterCategory(e.target.value)}
                  className="category-select"
                >
                  <option value="all">All Categories</option>
                  <option value="personal">Personal</option>
                  <option value="relationship">Relationship</option>
                  <option value="academic">Academic</option>
                  <option value="other">Other</option>
                </select>
              </div>
            </div>

            {postsLoading ? (
              <div className="loading">Loading confessions...</div>
            ) : (
              <div className="confessions-list">
                {currentConfessions.map(confession => (
                  <div key={confession.id} className="confession-card">
                    <div className="confession-header">
                      <h3>{confession.title}</h3>
                      <span className="category">{confession.category}</span>
                    </div>
                    <p className="confession-content">
                      {confession.content.substring(0, 150)}...
                    </p>
                    <div className="confession-footer">
                      <div className="stats">
                        <span>{confession.likes || 0} Likes</span>
                        <span>{confession.comments || 0} Comments</span>
                      </div>
                      <div className="actions">
                        <button onClick={() => setSelectedPost(confession)}>
                          View
                        </button>
                        <button onClick={() => navigate(`/edit-confession/${confession.id}`)}>
                          Edit
                        </button>
                        <button onClick={() => handleDeleteConfession(confession.id)}>
                          Delete
                        </button>
                      </div>
                      <div className="date">
                        {formatDate(confession.createdAt)}
                      </div>
                    </div>
                  </div>
                ))}

                {currentConfessions.length === 0 && (
                  <p className="no-confessions">No confessions found.</p>
                )}

                {totalPages > 1 && (
                  <div className="pagination">
                    <button 
                      onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                      disabled={currentPage === 1}
                    >
                      Previous
                    </button>
                    <span>Page {currentPage} of {totalPages}</span>
                    <button 
                      onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                      disabled={currentPage === totalPages}
                    >
                      Next
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Right Section - Profile Settings */}
          <div className="settings-section">
            <h2>Profile Settings</h2>
            
            {error && (
              <div className="error-message">
                <p>{error}</p>
              </div>
            )}

            <div className="profile-form">
              {isEditing ? (
                <form onSubmit={handleSubmit}>
                  <div className="form-group">
                    <label>Nickname</label>
                    <input
                      type="text"
                      name="nickname"
                      value={formData.nickname}
                      onChange={handleInputChange}
                      required
                      maxLength="10"
                    />
                  </div>

                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      required
                    />
                  </div>

                  <div className="form-actions">
                    <button type="submit" disabled={loading}>
                      {loading ? 'Saving...' : 'Save Changes'}
                    </button>
                    <button type="button" onClick={handleCancel}>
                      Cancel
                    </button>
                  </div>
                </form>
              ) : (
                <div className="profile-info">
                  <div className="info-group">
                    <label>Nickname</label>
                    <p>{userData?.nickname}</p>
                  </div>

                  <div className="info-group">
                    <label>Email</label>
                    <p>{userData?.email}</p>
                  </div>

                  <button className="edit-button" onClick={handleEdit}>
                    Edit Profile
                  </button>
                </div>
              )}

              <div className="password-section">
                <button 
                  className="password-toggle"
                  onClick={() => setShowPasswordSection(!showPasswordSection)}
                >
                  Change Password
                </button>

                {showPasswordSection && (
                  <form onSubmit={handlePasswordSubmit} className="password-form">
                    {passwordError && (
                      <div className="error-message">
                        <p>{passwordError}</p>
                      </div>
                    )}
                    {passwordSuccess && (
                      <div className="success-message">
                        <p>{passwordSuccess}</p>
                      </div>
                    )}

                    <div className="form-group">
                      <label>Current Password</label>
                      <input
                        type="password"
                        name="currentPassword"
                        value={passwordData.currentPassword}
                        onChange={handlePasswordChange}
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label>New Password</label>
                      <input
                        type="password"
                        name="newPassword"
                        value={passwordData.newPassword}
                        onChange={handlePasswordChange}
                        required
                        minLength="6"
                      />
                    </div>

                    <div className="form-group">
                      <label>Confirm New Password</label>
                      <input
                        type="password"
                        name="confirmPassword"
                        value={passwordData.confirmPassword}
                        onChange={handlePasswordChange}
                        required
                        minLength="6"
                      />
                    </div>

                    <button 
                      type="submit" 
                      disabled={passwordLoading}
                      className="change-password-button"
                    >
                      {passwordLoading ? 'Changing...' : 'Change Password'}
                    </button>
                  </form>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Confession Detail Modal */}
      {selectedPost && (
        <div className="modal" onClick={() => setSelectedPost(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <div className="modal-header">
              <h2>{selectedPost.title}</h2>
              <button className="close-button" onClick={() => setSelectedPost(null)}>
                ×
              </button>
            </div>
            
            <div className="modal-info">
              <div className="modal-metadata">
                <p>Category: {selectedPost.category}</p>
                <p>Created: {formatDate(selectedPost.createdAt)}</p>
              </div>
              
              <div className="modal-current-info">
                <p>Current Date and Time (UTC - YYYY-MM-DD HH:MM:SS formatted): {currentDateTime}</p>
                <p>Current User's Login: {userData?.nickname || '2025raj'}</p>
              </div>
            </div>

            <div className="modal-body">
              <p>{selectedPost.content}</p>
            </div>

            <div className="modal-stats">
              <span>{selectedPost.likes || 0} Likes</span>
              <span>{selectedPost.comments || 0} Comments</span>
            </div>

            <div className="modal-actions">
              <button 
                className="edit-button"
                onClick={() => {
                  navigate(`/edit-confession/${selectedPost.id}`);
                  setSelectedPost(null);
                }}
              >
                Edit
              </button>
              <button 
                className="delete-button"
                onClick={() => {
                  if (window.confirm('Are you sure you want to delete this confession?')) {
                    handleDeleteConfession(selectedPost.id);
                    setSelectedPost(null);
                  }
                }}
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Success Toast */}
      {passwordSuccess && (
        <div className="success-toast">
          <p>{passwordSuccess}</p>
        </div>
      )}

      {/* Error Toast */}
      {error && (
        <div className="error-toast">
          <p>{error}</p>
        </div>
      )}
    </div>
  );
};

export default Profilepage;
