import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { updateConfession } from '../../apis/Api';
import './EditConfession.css'; // Ensure the path is correct

const EditConfession = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const confession = location.state.confession;

  const [editContent, setEditContent] = useState({
    content: confession.content,
    title: confession.title,
    category: confession.category,
    imageUrl: confession.imageUrl,
  });

  const [selectedFile, setSelectedFile] = useState(null);

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditContent((prevContent) => ({
      ...prevContent,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setEditContent((prevContent) => ({
        ...prevContent,
        imageUrl: URL.createObjectURL(file),
      }));
    }
  };

  const handleEditSubmit = async (confessionId) => {
    try {
      const formData = new FormData();
      formData.append('content', editContent.content);
      formData.append('title', editContent.title);
      formData.append('category', editContent.category);
      if (selectedFile) {
        formData.append('image', selectedFile);
      }

      await updateConfession(confessionId, formData);
      navigate('/confessions');
    } catch (error) {
      console.error('Failed to update confession:', error);
    }
  };

  return (
    <div className="edit-confession-container">
      <h2>Edit Confession</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleEditSubmit(confession._id);
        }}
      >
        <input
          type="text"
          name="title"
          value={editContent.title}
          onChange={handleEditChange}
          className="edit-input"
          placeholder="Title"
        />
        <textarea
          name="content"
          value={editContent.content}
          onChange={handleEditChange}
          className="edit-textarea"
          placeholder="Content"
        />
        <input
          type="text"
          name="category"
          value={editContent.category}
          onChange={handleEditChange}
          className="edit-input"
          placeholder="Category"
        />
        <input
          type="file"
          name="image"
          onChange={handleImageChange}
          className="edit-input"
        />
        {editContent.imageUrl && (
          <img
            src={selectedFile ? editContent.imageUrl : `http://localhost:5000/${editContent.imageUrl.replace(/^C:\\uiux development\\backend\\/, '')}`}
            alt="Confession"
            className="confession-image-preview"
          />
        )}
        <button type="submit" className="save-button">
          Save
        </button>
        <button
          type="button"
          className="cancel-button"
          onClick={() => navigate('/confessions')}
        >
          Cancel
        </button>
      </form>
    </div>
  );
};

export default EditConfession;
