import React, { useEffect, useState } from 'react';
import { getUserProfile, updateUserProfile, changePassword } from '../../apis/Api';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './Profilepage.css';

const Profilepage = () => {
  // State for user data and UI
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeView, setActiveView] = useState('profile');
  const [currentDateTime, setCurrentDateTime] = useState('');
  
  // Profile editing states
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    nickname: '',
    email: ''
  });

  // Password change states
  const [showPasswordSection, setShowPasswordSection] = useState(false);
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [passwordError, setPasswordError] = useState('');
  const [passwordSuccess, setPasswordSuccess] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (!storedUser) {
          navigate('/login');
          return;
        }

        const response = await getUserProfile();
        if (response.data?.success && response.data?.data) {
          const profileData = response.data.data;
          setUserData(profileData);
          setFormData({
            nickname: profileData.nickname,
            email: profileData.email
          });
        }
      } catch (err) {
        console.error('Profile fetch error:', err);
        setError('Could not load profile data');
        toast.error('Failed to load profile data');
      } finally {
        setLoading(false);
      }
    };

    fetchUserProfile();

    // DateTime update
    const updateDateTime = () => {
      const now = new Date();
      const year = now.getUTCFullYear();
      const month = String(now.getUTCMonth() + 1).padStart(2, '0');
      const day = String(now.getUTCDate()).padStart(2, '0');
      const hours = String(now.getUTCHours()).padStart(2, '0');
      const minutes = String(now.getUTCMinutes()).padStart(2, '0');
      const seconds = String(now.getUTCSeconds()).padStart(2, '0');
      setCurrentDateTime(`${year}-${month}-${day} ${hours}:${minutes}:${seconds}`);
    };

    updateDateTime();
    const timer = setInterval(updateDateTime, 1000);

    return () => clearInterval(timer);
  }, [navigate]);
// Remove e.preventDefault() from handleInputChange
const handleInputChange = (e) => {
  const { name, value } = e.target;
  setFormData({
    ...formData,
    [name]: value
  });
};

// Remove e.preventDefault() from handlePasswordChange
const handlePasswordChange = (e) => {
  const { name, value } = e.target;
  setPasswordData({
    ...passwordData,
    [name]: value
  });
  setPasswordError('');
};

  const handleEdit = (e) => {
    e.preventDefault();
    setIsEditing(true);
    setFormData({
      nickname: userData.nickname,
      email: userData.email
    });
    setError(null);
  };

  const handleCancel = (e) => {
    e.preventDefault();
    setIsEditing(false);
    setFormData({
      nickname: userData.nickname,
      email: userData.email
    });
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);

      const response = await updateUserProfile(formData);
      if (response.data.success) {
        setUserData(response.data.user);
        setIsEditing(false);
        toast.success('Profile updated successfully');
      }
    } catch (err) {
      setError('Failed to update profile');
      toast.error('Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setPasswordError('');
    setPasswordSuccess('');

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setPasswordError('Passwords do not match');
      return;
    }

    try {
      setPasswordLoading(true);
      const response = await changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      if (response.data.success) {
        setPasswordSuccess('Password updated successfully');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        toast.success('Password updated successfully');
        
        setTimeout(() => {
          setShowPasswordSection(false);
          setPasswordSuccess('');
        }, 2000);
      }
    } catch (err) {
      setPasswordError('Failed to update password');
      toast.error('Failed to update password');
    } finally {
      setPasswordLoading(false);
    }
  };

  const ProfileView = () => (
    <div className="content-card">
      <h2>Profile Settings</h2>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Nickname:</label>
            <input
              type="text"
              name="nickname"
              value={formData.nickname}
              onChange={handleInputChange}
              required
              maxLength="10"
              pattern="^[a-zA-Z0-9_]+$"
              title="Nickname can only contain letters, numbers, and underscores"
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="button-group">
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={handleCancel}
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <>
          <div className="profile-info">
            <div className="info-group">
              <span className="label">Nickname:</span>
              <span className="value">{userData?.nickname}</span>
            </div>
            <div className="info-group">
              <span className="label">Email:</span>
              <span className="value">{userData?.email}</span>
            </div>
            <button 
              className="btn btn-primary mt-2"
              onClick={handleEdit}
            >
              Edit Profile
            </button>
          </div>

          <div className="password-section mt-4">
            <h3>Password Settings</h3>
            <button 
              className="btn btn-secondary mt-2"
              onClick={() => setShowPasswordSection(!showPasswordSection)}
            >
              {showPasswordSection ? 'Hide Password Form' : 'Change Password'}
            </button>

            {showPasswordSection && (
              <form onSubmit={handlePasswordSubmit} className="password-form mt-3">
                {passwordError && <div className="error-message">{passwordError}</div>}
                {passwordSuccess && <div className="success-message">{passwordSuccess}</div>}
                
                <div className="form-group">
                  <label>Current Password:</label>
                  <input
                    type="password"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>New Password:</label>
                  <input
                    type="password"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength="6"
                  />
                </div>
                <div className="form-group">
                  <label>Confirm Password:</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength="6"
                  />
                </div>
                <div className="button-group">
                  <button 
                    type="submit" 
                    className="btn btn-primary"
                    disabled={passwordLoading}
                  >
                    {passwordLoading ? 'Updating...' : 'Update Password'}
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => {
                      setShowPasswordSection(false);
                      setPasswordData({
                        currentPassword: '',
                        newPassword: '',
                        confirmPassword: ''
                      });
                      setPasswordError('');
                    }}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            )}
          </div>
        </>
      )}
    </div>
  );

  const ConfessionsView = () => (
    <div className="content-card">
      <h2>Your Confessions</h2>
      <p>Confessions view coming soon...</p>
    </div>
  );

  if (loading && !userData) {
    return (
      <div className="profile-page">
        <div className="loading-spinner">
          <div className="spinner"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="current-info-display">
        <div className="time-display">
          Current Date and Time (UTC - YYYY-MM-DD HH:MM:SS formatted): {currentDateTime}
        </div>
        <div className="user-display">
          Current User's Login: {userData?.nickname}
        </div>
      </div>
      <div className="main-layout">
        <div className="main-content">
          {activeView === 'profile' ? <ProfileView /> : <ConfessionsView />}
        </div>

        <div className="right-sidebar">
          <button
            className={`sidebar-button ${activeView === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveView('profile')}
          >
            Profile Settings
          </button>
          <button
            className={`sidebar-button ${activeView === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveView('posts')}
          >
            Your Confessions
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profilepage;
