// src/App.js
import React, { useState, useEffect, Suspense, useCallback, useMemo } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import LoadingSpinner from './components/LoadingSpinner';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import { isLoggedIn, logout } from './apis/Api';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// Lazy load components with error boundaries
const lazyLoad = (importFunc) => {
  const Component = React.lazy(importFunc);
  return (props) => (
    <ErrorBoundary>
      <Suspense fallback={<LoadingSpinner size="large" fullScreen />}>
        <Component {...props} />
      </Suspense>
    </ErrorBoundary>
  );
};

// Lazy loaded components
const Homepage = lazyLoad(() => import('./pages/homepage/Homepage'));
const Loginpage = lazyLoad(() => import('./pages/loginpage/Loginpage'));
const Registerpage = lazyLoad(() => import('./pages/registerpage/Registerpage'));
const Profilepage = lazyLoad(() => import('./pages/profile/Profilepage'));
const Communities = lazyLoad(() => import('./pages/communities/Communities'));
const ContactUs = lazyLoad(() => import('./pages/contact/ContactUs'));
const CreateConfession = lazyLoad(() => import('./pages/confession/CreateConfession'));

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('Error caught by boundary:', error, errorInfo);
    // You could send this to an error reporting service
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-container">
          <h1>Oops! Something went wrong.</h1>
          <p>{this.state.error?.message}</p>
          <button onClick={() => window.location.reload()}>
            Refresh Page
          </button>
        </div>
      );
    }
    return this.props.children;
  }
}

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const location = useLocation();
  const authenticated = isLoggedIn();
  const lastActive = localStorage.getItem('lastActive');
  const sessionTimeout = 24 * 60 * 60 * 1000; // 24 hours

  useEffect(() => {
    if (authenticated && lastActive) {
      const timeElapsed = Date.now() - new Date(lastActive).getTime();
      if (timeElapsed > sessionTimeout) {
        toast.error('Session expired. Please login again.');
        logout();
      }
    }
  }, [authenticated, lastActive]);

  if (!authenticated) {
    return <Navigate to="/login" state={{ from: location.pathname }} replace />;
  }

  return children;
};

// Public Route Component
const PublicRoute = ({ children }) => {
  const location = useLocation();
  const authenticated = isLoggedIn();

  if (authenticated) {
    return <Navigate to={location.state?.from || '/'} replace />;
  }

  return children;
};

// Not Found Component
const NotFound = () => (
  <div className="not-found">
    <h1>404 - Page Not Found</h1>
    <p>The page you're looking for doesn't exist.</p>
    <button onClick={() => window.history.back()}>Go Back</button>
  </div>
);

// Main App Component
function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(isLoggedIn);
  const [isLoading, setIsLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);

  // Check authentication status
  const checkAuthStatus = useCallback(() => {
    const authenticated = isLoggedIn();
    setIsAuthenticated(authenticated);
    
    if (authenticated) {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      setCurrentUser(user);
      localStorage.setItem('lastActive', new Date().toISOString());
    } else {
      setCurrentUser(null);
    }
    
    setIsLoading(false);
  }, []);

  // Initialize auth check
  useEffect(() => {
    checkAuthStatus();
    window.addEventListener('storage', checkAuthStatus);
    
    const interval = setInterval(checkAuthStatus, 60000); // Check every minute
    
    return () => {
      window.removeEventListener('storage', checkAuthStatus);
      clearInterval(interval);
    };
  }, [checkAuthStatus]);

  // Handle login/logout
  const handleLogin = useCallback(() => {
    setIsAuthenticated(true);
    checkAuthStatus();
    toast.success('Welcome back!');
  }, [checkAuthStatus]);

  const handleLogout = useCallback(() => {
    logout();
    setIsAuthenticated(false);
    setCurrentUser(null);
    toast.info('You have been logged out.');
  }, []);

  // Memoized route configurations
  const routeConfig = useMemo(() => [
    { path: '/', element: <Homepage /> },
    { path: '/communities', element: <Communities /> },
    { path: '/contact', element: <ContactUs /> },
    {
      path: '/login',
      element: (
        <PublicRoute>
          <Loginpage onLogin={handleLogin} />
        </PublicRoute>
      )
    },
    {
      path: '/register',
      element: (
        <PublicRoute>
          <Registerpage />
        </PublicRoute>
      )
    },
    {
      path: '/profile',
      element: (
        <ProtectedRoute>
          <Profilepage onLogout={handleLogout} user={currentUser} />
        </ProtectedRoute>
      )
    },
    {
      path: '/create-confession',
      element: (
        <ProtectedRoute>
          <CreateConfession user={currentUser} />
        </ProtectedRoute>
      )
    },
    { path: '*', element: <NotFound /> }
  ], [handleLogin, handleLogout, currentUser]);

  if (isLoading) {
    return <LoadingSpinner size="large" fullScreen />;
  }

  return (
    <Router>
      <div className="app-container">
        <Navbar 
          isAuthenticated={isAuthenticated} 
          onLogout={handleLogout}
          user={currentUser}
        />
        
        <main className="main-content">
          <Routes>
            {routeConfig.map((route, index) => (
              <Route 
                key={index}
                path={route.path}
                element={route.element}
              />
            ))}
          </Routes>
        </main>

        <Footer />
        
        <ToastContainer
          position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="colored"
        />
      </div>
    </Router>
  );
}

export default App;
