import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { loginApi } from "../../apis/Api";
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/LoadingSpinner/LoadingSpinner';
import './Loginpage.css';

const getCurrentUTCDateTime = () => {
  const now = new Date();
  return now.toISOString().slice(0, 19).replace('T', ' ');
};

const Loginpage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    timestamp: getCurrentUTCDateTime(),
    lastActive: getCurrentUTCDateTime()
  });

  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({
    email: '',
    password: '',
    general: ''
  });

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Check if redirected due to session expiry
    const searchParams = new URLSearchParams(location.search);
    if (searchParams.get('session') === 'expired') {
      toast.info('Your session has expired. Please login again.', {
        toastId: 'session-expired-login'
      });
    }

    // Check if there's a remembered email
    const rememberedEmail = localStorage.getItem('rememberedEmail');
    if (rememberedEmail) {
      setFormData(prev => ({
        ...prev,
        email: rememberedEmail
      }));
    }

    // Clear any existing session data
    localStorage.removeItem('sessionWarningShown');
    document.title = 'Login - Confession App';

    return () => {
      toast.dismiss('session-expired-login');
    };
  }, [location.search]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    const currentTime = getCurrentUTCDateTime();

    setFormData(prev => ({
      ...prev,
      [name]: value,
      timestamp: currentTime,
      lastActive: currentTime
    }));

    setErrors(prev => ({
      ...prev,
      [name]: '',
      general: ''
    }));
  };

  const validate = () => {
    const newErrors = {};
    const currentTime = getCurrentUTCDateTime();

    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Email is invalid";
    }

    if (!formData.password.trim()) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 8) {
      newErrors.password = "Password must be at least 8 characters";
    }

    setErrors(prev => ({
      ...prev,
      ...newErrors
    }));

    // Update timestamp even during validation
    setFormData(prev => ({
      ...prev,
      timestamp: currentTime,
      lastActive: currentTime
    }));

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validate()) return;

    try {
      setLoading(true);
      const currentTime = getCurrentUTCDateTime();
      
      const loginData = {
        ...formData,
        timestamp: currentTime,
        lastActive: currentTime,
        deviceInfo: {
          userAgent: navigator.userAgent,
          platform: navigator.platform,
          language: navigator.language,
          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone
        }
      };

      const response = await loginApi(loginData);

      if (response.data.success) {
        // Update local storage with user data and timestamps
        localStorage.setItem('token', response.data.data.token);
        localStorage.setItem('user', JSON.stringify({
          ...response.data.data.user,
          lastLoginTime: currentTime,
          lastActive: currentTime
        }));
        
        // Handle "Remember Me" functionality
        if (document.getElementById('rememberMe').checked) {
          localStorage.setItem('rememberedEmail', formData.email);
        } else {
          localStorage.removeItem('rememberedEmail');
        }

        toast.success(`Welcome back, ${response.data.data.user.nickname || 'raj2080'}!`, {
          position: "top-right",
          autoClose: 3000
        });

        onLogin();
        navigate('/');
      }
    } catch (error) {
      console.error('Login error:', {
        timestamp: getCurrentUTCDateTime(),
        error: error.message
      });
      
      const errorMessage = error.response?.data?.message || 'Login failed. Please try again.';
      toast.error(errorMessage);
      
      setErrors(prev => ({
        ...prev,
        general: errorMessage
      }));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h1>Welcome Back</h1>
          <p>Please sign in to continue</p>
          <p className="timestamp">Last Updated: {formData.timestamp}</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {errors.general && (
            <div className="error-message">
              <i className="fas fa-exclamation-circle"></i>
              <span>{errors.general}</span>
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">
              <i className="fas fa-envelope"></i>
              Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Enter your email"
              className={`form-input ${errors.email ? 'error' : ''}`}
              autoComplete="email"
              aria-describedby="emailError"
            />
            {errors.email && (
              <span className="error-text" id="emailError">
                {errors.email}
              </span>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="password">
              <i className="fas fa-lock"></i>
              Password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              className={`form-input ${errors.password ? 'error' : ''}`}
              autoComplete="current-password"
              aria-describedby="passwordError"
            />
            {errors.password && (
              <span className="error-text" id="passwordError">
                {errors.password}
              </span>
            )}
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input
                type="checkbox"
                id="rememberMe"
                defaultChecked={!!localStorage.getItem('rememberedEmail')}
              />
              <span>Remember me</span>
            </label>
            <Link to="/forgot-password" className="forgot-password">
              Forgot Password?
            </Link>
          </div>

          <button 
            type="submit" 
            className={`login-button ${loading ? 'loading' : ''}`}
            disabled={loading}
          >
            {loading ? (
              <LoadingSpinner fullScreen={false} />
            ) : (
              'Sign In'
            )}
          </button>
        </form>

        <div className="login-footer">
          <p>Don't have an account? {' '}
            <Link to="/register" className="signup-link">
              Sign up
            </Link>
          </p>
          <p className="session-info">
            Session started at: {getCurrentUTCDateTime()}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Loginpage;
