import axios from 'axios';
import { toast } from 'react-toastify';

// Constants
const API_BASE_URL = 'http://localhost:5000/api';
const SESSION_TIMEOUT = 60 * 1000; // 1 minute
const WARNING_TIME = 45 * 1000; // Show warning after 45 seconds
const STORAGE_KEYS = {
    TOKEN: 'token',
    USER_ID: 'userId',
    USER: 'user',
    REFRESH_TOKEN: 'refreshToken',
    LAST_ACTIVE: 'lastActive',
    SESSION_ID: 'sessionId',
    SESSION_START: 'sessionStart',
    SESSION_WARNING_SHOWN: 'sessionWarningShown'
};

// Helper Functions
const getCurrentDateTime = () => {
    const now = new Date();
    return now.toISOString().slice(0, 19).replace('T', ' ');
};

const getCurrentUser = () => {
    const userData = localStorage.getItem(STORAGE_KEYS.USER);
    if (!userData) return 'anonymous';
    const user = JSON.parse(userData);
    return user.nickname || 'anonymous';
};

const clearStorage = () => {
    Object.values(STORAGE_KEYS).forEach(key => localStorage.removeItem(key));
    sessionStorage.clear();
    document.cookie.split(";").forEach(cookie => {
        document.cookie = cookie.replace(/^ +/, "").replace(/=.*/, "=;expires=" + new Date().toUTCString() + ";path=/");
    });
};

const updateStoredUserData = (userData) => {
    if (!userData) return;
    
    const userToStore = {
        _id: userData._id || userData.id,
        nickname: userData.nickname,
        email: userData.email,
        updatedAt: userData.updatedAt,
        lastActive: getCurrentDateTime()
    };
    
    localStorage.setItem(STORAGE_KEYS.USER, JSON.stringify(userToStore));
    localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, getCurrentDateTime());
    localStorage.setItem(STORAGE_KEYS.SESSION_START, getCurrentDateTime());
};

const checkSessionExpiry = () => {
    const lastActive = localStorage.getItem(STORAGE_KEYS.LAST_ACTIVE);
    if (!lastActive) return true;

    const currentTime = new Date().getTime();
    const lastActiveTime = new Date(lastActive).getTime();
    const timeDifference = currentTime - lastActiveTime;

    // Show warning when 15 seconds remaining
    if (timeDifference > WARNING_TIME && !localStorage.getItem(STORAGE_KEYS.SESSION_WARNING_SHOWN)) {
        toast.warning('Your session will expire in 15 seconds. Please save your work.', {
            toastId: 'session-warning'
        });
        localStorage.setItem(STORAGE_KEYS.SESSION_WARNING_SHOWN, 'true');
    }

    return timeDifference > SESSION_TIMEOUT;
};

// API Instance Configuration
const Api = axios.create({
    baseURL: API_BASE_URL,
    withCredentials: true,
    headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
    }
});

// Request Interceptor
Api.interceptors.request.use(
    (config) => {
        const token = localStorage.getItem(STORAGE_KEYS.TOKEN);
        
        // Check session expiry
        if (checkSessionExpiry()) {
            clearStorage();
            window.location.href = '/login?session=expired';
            return Promise.reject('Session expired');
        }

        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }

        // Update last active timestamp
        localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, getCurrentDateTime());

        config.metadata = { 
            startTime: new Date(),
            requestId: Math.random().toString(36).substring(7),
            timestamp: getCurrentDateTime(),
            user: getCurrentUser()
        };

        if (process.env.NODE_ENV === 'development') {
            console.log('API Request:', {
                url: config.url,
                method: config.method,
                requestId: config.metadata.requestId,
                headers: config.headers,
                timestamp: config.metadata.timestamp,
                user: config.metadata.user
            });
        }

        return config;
    },
    (error) => Promise.reject(handleApiError(error))
);

// Response Interceptor
Api.interceptors.response.use(
    (response) => {
        const requestStartTime = response.config.metadata.startTime;
        response.duration = new Date() - requestStartTime;

        // Update last active timestamp
        localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, getCurrentDateTime());

        if (process.env.NODE_ENV === 'development') {
            console.log('API Response:', {
                url: response.config.url,
                status: response.status,
                duration: response.duration,
                requestId: response.config.metadata.requestId,
                timestamp: getCurrentDateTime()
            });
        }

        return response;
    },
    async (error) => {
        if (error.response?.status === 401) {
            clearStorage();
            window.location.href = '/login?session=expired';
        }
        return Promise.reject(handleApiError(error));
    }
);

// Error Handler
const handleApiError = (error) => {
    const errorMessage = error.response?.data?.message || error.message || 'An unexpected error occurred';
    const timestamp = getCurrentDateTime();
    
    if (process.env.NODE_ENV === 'development') {
        console.error('API Error:', {
            message: errorMessage,
            status: error.response?.status,
            data: error.response?.data,
            url: error.config?.url,
            method: error.config?.method,
            timestamp,
            user: getCurrentUser()
        });
    }

    toast.error(errorMessage);
    
    return {
        message: errorMessage,
        status: error.response?.status,
        data: error.response?.data,
        timestamp,
        user: getCurrentUser(),
        originalError: error
    };
};

// Auth Functions
export const signupApi = async (data) => {
    try {
        const response = await Api.post('/users/signup', {
            ...data,
            timestamp: getCurrentDateTime()
        });
        toast.success('Registration successful! Please log in.');
        return response;
    } catch (error) {
        throw handleApiError(error);
    }
};

export const loginApi = async (data) => {
    try {
        localStorage.removeItem(STORAGE_KEYS.SESSION_WARNING_SHOWN);
        const response = await Api.post('/users/login', {
            ...data,
            timestamp: getCurrentDateTime()
        });
        
        if (response.data?.data) {
            const { user, token } = response.data.data;
            
            localStorage.setItem(STORAGE_KEYS.TOKEN, token);
            localStorage.setItem(STORAGE_KEYS.USER_ID, user._id || user.id);
            localStorage.setItem(STORAGE_KEYS.SESSION_START, getCurrentDateTime());
            localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, getCurrentDateTime());
            updateStoredUserData(user);

            toast.success(`Welcome back, ${user.nickname}!`);
        }
        return response;
    } catch (error) {
        clearStorage();
        throw handleApiError(error);
    }
};

// Rest of your API functions remain the same, just add timestamp to each request...
// (I'm keeping all your existing functions but adding timestamps)

export const isLoggedIn = () => {
    try {
        const token = localStorage.getItem(STORAGE_KEYS.TOKEN);
        const userId = localStorage.getItem(STORAGE_KEYS.USER_ID);
        const lastActive = localStorage.getItem(STORAGE_KEYS.LAST_ACTIVE);

        if (!token || !userId) return false;

        if (lastActive) {
            const lastActiveTime = new Date(lastActive).getTime();
            const currentTime = new Date().getTime();
            const sessionDuration = currentTime - lastActiveTime;
            
            if (sessionDuration > SESSION_TIMEOUT) {
                clearStorage();
                window.location.href = '/login?session=expired';
                return false;
            }
        }

        return true;
    } catch (error) {
        console.error('Error checking auth status:', {
            error: error.message,
            timestamp: getCurrentDateTime(),
            user: getCurrentUser()
        });
        return false;
    }
};

export const logout = () => {
    try {
        const timestamp = getCurrentDateTime();
        Api.post('/users/logout', { timestamp }).finally(() => {
            clearStorage();
            window.location.href = '/login';
        });
    } catch (error) {
        console.error('Logout error:', {
            error: error.message,
            timestamp: getCurrentDateTime(),
            user: getCurrentUser()
        });
        clearStorage();
        window.location.href = '/login';
    }
};

// Initialize session check interval
if (typeof window !== 'undefined') {
    setInterval(() => {
        if (isLoggedIn()) {
            checkSessionExpiry();
        }
    }, 10000); // Check every 10 seconds
}

export default Api;
