import axios from 'axios';

// Creating backend configuration
const Api = axios.create({
    baseURL: 'http://localhost:5000/api',
    withCredentials: true,
    headers: {
        'Content-Type': 'application/json'
    }
});

export const signupApi = (data) => Api.post('users/signup', data);

// export const loginApi = (data) => Api.post('users/login', data); 
// // src/apis/Api.js

export const loginApi = async (data) => {
    const response = await Api.post('users/login', data);
    const userId = response.data.userId; // Assuming the response contains the userId
    localStorage.setItem('userId', userId); // Store the user ID in local storage
    return response;
  };

// Add the new function to fetch user profile
export const getUserProfile = (userId) => Api.get(`users/profile/${userId}`);
