import React, { useState, useEffect, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import LoadingSpinner from './components/LoadingSpinner/LoadingSpinner';
import SessionManager from './components/SessionManager/SessionManager';
import { isLoggedIn, getStoredUserData } from './apis/Api';
import { getSystemConfig } from './config/constants';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// Lazy load components with dynamic imports
const componentImports = {
  Homepage: () => import('./pages/homepage/Homepage'),
  Loginpage: () => import('./pages/loginpage/Loginpage'),
  Registerpage: () => import('./pages/registerpage/Registerpage'),
  Profilepage: () => import('./pages/profile/Profilepage'),
  Communities: () => import('./pages/communities/Communities'),
  ContactUs: () => import('./pages/contactus/ContactUs'),
  CreateConfession: () => import('./pages/confession/CreateConfession'),
  EditConfession: () => import('./pages/confession/EditConfession'),
  Forgetpassword: () => import('./pages/forgetpass/Forgetpassword'),
  ResetPassword: () => import('./pages/forgetpass/Resetpassword')
};

// Create lazy components dynamically
const lazyComponents = Object.fromEntries(
  Object.entries(componentImports).map(([key, importFn]) => [
    key,
    React.lazy(importFn)
  ])
);

// Helper function for current timestamp
const getCurrentUTCDateTime = () => {
  return new Date().toISOString().slice(0, 19).replace('T', ' ');
};

// Protected Route Component with session awareness
const ProtectedRoute = ({ children }) => {
  const location = useLocation();
  const authenticated = isLoggedIn();
  const currentTime = getCurrentUTCDateTime();

  useEffect(() => {
    if (!authenticated) {
      localStorage.setItem('redirectUrl', location.pathname);
      localStorage.setItem('lastFailedAuth', currentTime);
    }
  }, [authenticated, location, currentTime]);

  if (!authenticated) {
    return <Navigate to="/login" state={{ from: location.pathname, timestamp: currentTime }} replace />;
  }

  return children;
};

// Public Route Component with timestamp tracking
const PublicRoute = ({ children }) => {
  const location = useLocation();
  const authenticated = isLoggedIn();
  const currentTime = getCurrentUTCDateTime();

  if (authenticated) {
    const redirectUrl = localStorage.getItem('redirectUrl') || '/';
    localStorage.removeItem('redirectUrl');
    localStorage.setItem('lastRedirect', currentTime);
    return <Navigate to={redirectUrl} replace />;
  }

  return children;
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(isLoggedIn);
  const [isLoading, setIsLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(getStoredUserData);
  const [lastActive, setLastActive] = useState(getCurrentUTCDateTime);
  const [systemConfig] = useState(getSystemConfig);

  useEffect(() => {
    const checkAuthStatus = () => {
      const currentTime = getCurrentUTCDateTime();
      const authenticated = isLoggedIn();
      setIsAuthenticated(authenticated);

      if (authenticated) {
        const userData = getStoredUserData();
        setCurrentUser(userData);
        localStorage.setItem('lastActive', currentTime);
        setLastActive(currentTime);
      } else {
        setCurrentUser(null);
      }

      setIsLoading(false);
    };

    const activityEvents = ['mousemove', 'keydown', 'click', 'scroll', 'touchstart'];
    const handleActivity = () => {
      const currentTime = getCurrentUTCDateTime();
      setLastActive(currentTime);
      localStorage.setItem('lastActive', currentTime);
    };

    // Set up activity monitoring
    activityEvents.forEach(event => {
      window.addEventListener(event, handleActivity);
    });

    // Initial check
    checkAuthStatus();

    // Regular auth check interval
    const authCheckInterval = setInterval(checkAuthStatus, systemConfig.SESSION_CHECK_INTERVAL);

    // Storage event listener for cross-tab synchronization
    window.addEventListener('storage', checkAuthStatus);

    return () => {
      activityEvents.forEach(event => {
        window.removeEventListener(event, handleActivity);
      });
      window.removeEventListener('storage', checkAuthStatus);
      clearInterval(authCheckInterval);
    };
  }, [systemConfig.SESSION_CHECK_INTERVAL]);

  const handleLogin = () => {
    const currentTime = getCurrentUTCDateTime();
    setIsAuthenticated(true);
    const userData = getStoredUserData();
    setCurrentUser(userData);
    localStorage.setItem('lastLogin', currentTime);
    setLastActive(currentTime);
  };

  const handleLogout = () => {
    const currentTime = getCurrentUTCDateTime();
    Object.keys(localStorage).forEach(key => localStorage.removeItem(key));
    sessionStorage.clear();
    setIsAuthenticated(false);
    setCurrentUser(null);
    localStorage.setItem('lastLogout', currentTime);
  };

  if (isLoading) {
    return <LoadingSpinner fullScreen />;
  }

  return (
    <Router>
      <div className="app-container">
        <SessionManager 
          lastActive={lastActive}
          currentUser={currentUser}
          onSessionExpired={handleLogout}
        />

        <Navbar 
          isAuthenticated={isAuthenticated} 
          onLogout={handleLogout}
          user={currentUser}
          lastActive={lastActive}
        />

        <main className="main-content">
          <Suspense fallback={<LoadingSpinner />}>
            <Routes>
              {/* Public Routes */}
              <Route path="/" element={<lazyComponents.Homepage />} />
              <Route path="/communities" element={<lazyComponents.Communities />} />
              <Route path="/contact" element={<lazyComponents.ContactUs />} />

              {/* Auth Routes */}
              <Route
                path="/login"
                element={
                  <PublicRoute>
                    <lazyComponents.Loginpage onLogin={handleLogin} />
                  </PublicRoute>
                }
              />
              <Route
                path="/register"
                element={
                  <PublicRoute>
                    <lazyComponents.Registerpage />
                  </PublicRoute>
                }
              />
              <Route
                path="/forgot-password"
                element={
                  <PublicRoute>
                    <lazyComponents.Forgetpassword />
                  </PublicRoute>
                }
              />
              <Route
                path="/reset-password/:token"
                element={
                  <PublicRoute>
                    <lazyComponents.ResetPassword />
                  </PublicRoute>
                }
              />

              {/* Protected Routes */}
              <Route
                path="/profile"
                element={
                  <ProtectedRoute>
                    <lazyComponents.Profilepage 
                      onLogout={handleLogout} 
                      user={currentUser}
                      lastActive={lastActive}
                    />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/create-confession"
                element={
                  <ProtectedRoute>
                    <lazyComponents.CreateConfession 
                      user={currentUser}
                      timestamp={getCurrentUTCDateTime()}
                    />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/edit-confession/:id"
                element={
                  <ProtectedRoute>
                    <lazyComponents.EditConfession 
                      user={currentUser}
                      timestamp={getCurrentUTCDateTime()}
                    />
                  </ProtectedRoute>
                }
              />

              {/* Confession Routes */}
              <Route
                path="/confessions"
                element={
                  <Suspense fallback={<LoadingSpinner />}>
                    <lazyComponents.Homepage />
                  </Suspense>
                }
              />

              {/* 404 Route */}
              <Route
                path="*"
                element={
                  <div className="error-page">
                    <h1>404 - Page Not Found</h1>
                    <p>The page you're looking for doesn't exist.</p>
                    <p>Last checked: {getCurrentUTCDateTime()}</p>
                    <button onClick={() => window.history.back()}>
                      Go Back
                    </button>
                  </div>
                }
              />
            </Routes>
          </Suspense>
        </main>

        <Footer timestamp={getCurrentUTCDateTime()} />

        <ToastContainer
          position="top-right"
          autoClose={5000}
          hideProgressBar={false}
          newestOnTop
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="colored"
        />
      </div>
    </Router>
  );
}

export default App;
