import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  getUserProfile, 
  updateUserProfile, 
  changePassword, 
  getUserConfessions,
  deleteConfession,
  getStoredUserData
} from '../../apis/Api';
import { toast } from 'react-toastify';
import './Profilepage.css';

const Profilepage = () => {
  // State Management
  const [activeView, setActiveView] = useState('profile');
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentDateTime, setCurrentDateTime] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [userConfessions, setUserConfessions] = useState([]);
  const [showPasswordSection, setShowPasswordSection] = useState(false);

  // Form States
  const [formData, setFormData] = useState({
    nickname: '',
    email: ''
  });
  
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const navigate = useNavigate();

  // Initialize data and setup timers
  useEffect(() => {
    const initializeData = async () => {
      try {
        // Check for stored user data first
        const storedUser = getStoredUserData();
        if (!storedUser) {
          navigate('/login');
          return;
        }

        // Fetch fresh data
        const [profileRes, confessionsRes] = await Promise.all([
          getUserProfile(),
          getUserConfessions()
        ]);

        if (profileRes.data?.success) {
          setUserData(profileRes.data.data);
          setFormData({
            nickname: profileRes.data.data.nickname,
            email: profileRes.data.data.email
          });
        }

        if (confessionsRes.data?.success) {
          setUserConfessions(confessionsRes.data.confessions);
        }
      } catch (err) {
        toast.error('Failed to load profile data');
        setError('Could not load profile data');
      } finally {
        setLoading(false);
      }
    };

    initializeData();

    // Setup datetime update
    const updateDateTime = () => {
      const now = new Date();
      const year = now.getUTCFullYear();
      const month = String(now.getUTCMonth() + 1).padStart(2, '0');
      const day = String(now.getUTCDate()).padStart(2, '0');
      const hours = String(now.getUTCHours()).padStart(2, '0');
      const minutes = String(now.getUTCMinutes()).padStart(2, '0');
      const seconds = String(now.getUTCSeconds()).padStart(2, '0');
      setCurrentDateTime(`${year}-${month}-${day} ${hours}:${minutes}:${seconds}`);
    };

    updateDateTime();
    const timer = setInterval(updateDateTime, 1000);
    return () => clearInterval(timer);
  }, [navigate]);

  // Event Handlers
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({ ...prev, [name]: value }));
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await updateUserProfile(formData);
      
      if (response.data.success) {
        setUserData(response.data.user);
        setIsEditing(false);
        toast.success('Profile updated successfully');
      }
    } catch (err) {
      toast.error('Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordUpdate = async (e) => {
    e.preventDefault();
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }

    try {
      const response = await changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      if (response.data.success) {
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        setShowPasswordSection(false);
        toast.success('Password updated successfully');
      }
    } catch (err) {
      toast.error('Failed to update password');
    }
  };

  const handleDeleteConfession = async (confessionId) => {
    if (window.confirm('Are you sure you want to delete this confession?')) {
      try {
        await deleteConfession(confessionId);
        setUserConfessions(prev => prev.filter(conf => conf._id !== confessionId));
        toast.success('Confession deleted successfully');
      } catch (err) {
        toast.error('Failed to delete confession');
      }
    }
  };

  // Component Views
  const ProfileView = () => (
    <div className="content-card">
      <h2>Profile Settings</h2>
      {isEditing ? (
        <form onSubmit={handleProfileUpdate}>
          <div className="form-group">
            <label>Nickname:</label>
            <input
              type="text"
              name="nickname"
              value={formData.nickname}
              onChange={handleInputChange}
              required
              maxLength="10"
              pattern="^[a-zA-Z0-9_]+$"
              title="Nickname can only contain letters, numbers, and underscores"
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="button-group">
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => setIsEditing(false)}
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <>
          <div className="profile-info">
            <div className="info-row">
              <span className="label">Nickname:</span>
              <span className="value">{userData?.nickname}</span>
            </div>
            <div className="info-row">
              <span className="label">Email:</span>
              <span className="value">{userData?.email}</span>
            </div>
            <button 
              className="btn btn-primary mt-2"
              onClick={() => setIsEditing(true)}
            >
              Edit Profile
            </button>
          </div>

          <div className="password-section">
            <h3>Password Settings</h3>
            <button 
              className="btn btn-secondary"
              onClick={() => setShowPasswordSection(!showPasswordSection)}
            >
              Change Password
            </button>

            {showPasswordSection && (
              <form onSubmit={handlePasswordUpdate} className="password-form mt-2">
                <div className="form-group">
                  <label>Current Password:</label>
                  <input
                    type="password"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>New Password:</label>
                  <input
                    type="password"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength="6"
                  />
                </div>
                <div className="form-group">
                  <label>Confirm Password:</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength="6"
                  />
                </div>
                <div className="button-group">
                  <button type="submit" className="btn btn-primary">
                    Update Password
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => setShowPasswordSection(false)}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            )}
          </div>
        </>
      )}
    </div>
  );

  const ConfessionsView = () => (
    <div className="posts-container">
      <h2>Your Confessions</h2>
      {userConfessions.length > 0 ? (
        userConfessions.map(confession => (
          <div key={confession._id} className="post-card">
            <div className="post-header">
              <h3>{confession.title}</h3>
              <span className="post-category">{confession.category}</span>
            </div>
            <p className="post-content">{confession.content}</p>
            <div className="post-footer">
              <div className="post-stats">
                <span>{confession.likes?.length || 0} Likes</span>
                <span>{confession.comments?.length || 0} Comments</span>
              </div>
              <div className="post-actions">
                <button
                  className="btn btn-secondary btn-sm"
                  onClick={() => navigate(`/edit-confession/${confession._id}`)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteConfession(confession._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <div className="no-posts">
          <p>No confessions found</p>
          <button 
            className="btn btn-primary"
            onClick={() => navigate('/new-confession')}
          >
            Create Your First Confession
          </button>
        </div>
      )}
    </div>
  );

  if (loading && !userData) {
    return (
      <div className="profile-page">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="current-info-display">
        <div className="time-display">
          Current Date and Time (UTC): {currentDateTime}
        </div>
        <div className="user-display">
          Current User's Login: {userData?.nickname}
        </div>
      </div>

      <div className="main-layout">
        <div className="main-content">
          {activeView === 'profile' ? <ProfileView /> : <ConfessionsView />}
        </div>

        <div className="right-sidebar">
          <button
            className={`sidebar-button ${activeView === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveView('profile')}
          >
            Profile Settings
          </button>
          <button
            className={`sidebar-button ${activeView === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveView('posts')}
          >
            Your Confessions
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profilepage;
