import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Profilepage.css';

const Profilepage = () => {
  const [currentDateTime, setCurrentDateTime] = useState('');
  const [activeView, setActiveView] = useState('profile'); // 'profile' or 'posts'
  const [userData, setUserData] = useState({
    nickname: '2025raj',
    email: 'user@example.com'
  });
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    nickname: '',
    email: ''
  });
  const [userPosts, setUserPosts] = useState([
    {
      id: 1,
      title: "First Confession",
      content: "This is my first confession content...",
      category: "Personal",
      date: "2024-01-20",
      likes: 5,
      comments: 3
    },
    // Add more sample posts as needed
  ]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  // Update datetime every second
  useEffect(() => {
    const updateDateTime = () => {
      const now = new Date();
      const year = now.getUTCFullYear();
      const month = String(now.getUTCMonth() + 1).padStart(2, '0');
      const day = String(now.getUTCDate()).padStart(2, '0');
      const hours = String(now.getUTCHours()).padStart(2, '0');
      const minutes = String(now.getUTCMinutes()).padStart(2, '0');
      const seconds = String(now.getUTCSeconds()).padStart(2, '0');
      setCurrentDateTime(`${year}-${month}-${day} ${hours}:${minutes}:${seconds}`);
    };

    updateDateTime();
    const interval = setInterval(updateDateTime, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();
    try {
      // API call would go here
      setUserData(formData);
      setIsEditing(false);
      setSuccess('Profile updated successfully!');
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError('Failed to update profile');
      setTimeout(() => setError(''), 3000);
    }
  };

  const ProfileCard = () => (
    <div className="content-card">
      <h2>Profile Settings</h2>
      {isEditing ? (
        <form onSubmit={handleEditSubmit}>
          <div className="form-group">
            <label>Nickname:</label>
            <input
              type="text"
              name="nickname"
              value={formData.nickname}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="button-group">
            <button type="submit" className="btn btn-primary">Save</button>
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => setIsEditing(false)}
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <div className="profile-info">
          <div className="info-row">
            <span className="label">Nickname:</span>
            <span className="value">{userData.nickname}</span>
          </div>
          <div className="info-row">
            <span className="label">Email:</span>
            <span className="value">{userData.email}</span>
          </div>
          <button 
            className="btn btn-primary mt-2"
            onClick={() => {
              setFormData(userData);
              setIsEditing(true);
            }}
          >
            Edit Profile
          </button>
        </div>
      )}
    </div>
  );

  const PostCard = ({ post }) => (
    <div className="post-card">
      <div className="post-header">
        <h3>{post.title}</h3>
        <span className="post-category">{post.category}</span>
      </div>
      <p className="post-content">{post.content}</p>
      <div className="post-footer">
        <div className="post-stats">
          <span>{post.likes} Likes</span>
          <span>{post.comments} Comments</span>
        </div>
        <div className="post-date">{post.date}</div>
      </div>
    </div>
  );

  const PostsView = () => (
    <div className="posts-container">
      <h2>Your Posts</h2>
      {userPosts.length > 0 ? (
        userPosts.map(post => (
          <PostCard key={post.id} post={post} />
        ))
      ) : (
        <p className="no-posts">No posts found</p>
      )}
    </div>
  );

  return (
    <div className="profile-page">
      {/* Header Info */}
      <div className="current-info-display">
        <div className="time-display">
          Current Date and Time (UTC - YYYY-MM-DD HH:MM:SS formatted): {currentDateTime}
        </div>
        <div className="user-display">
          Current User's Login: {userData.nickname}
        </div>
      </div>

      <div className="main-layout">
        {/* Main Content Area */}
        <div className="main-content">
          {activeView === 'profile' ? <ProfileCard /> : <PostsView />}
        </div>

        {/* Right Sidebar */}
        <div className="right-sidebar">
          <button
            className={`sidebar-button ${activeView === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveView('profile')}
          >
            Profile Settings
          </button>
          <button
            className={`sidebar-button ${activeView === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveView('posts')}
          >
            Your Posts
          </button>
        </div>
      </div>

      {/* Messages */}
      {success && <div className="success-message">{success}</div>}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default Profilepage;
