import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { loginApi } from "../../apis/Api";
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/LoadingSpinner/LoadingSpinner';
import './Loginpage.css';

const Loginpage = ({ onLogin }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    timestamp: '',
    lastActive: ''
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({
    email: '',
    password: '',
    general: ''
  });

  const navigate = useNavigate();

  // Get current timestamp in UTC
  const getCurrentDateTime = () => {
    const now = new Date();
    return now.toISOString().slice(0, 19).replace('T', ' ');
  };

  useEffect(() => {
    // Check if there's an existing session
    const token = localStorage.getItem('token');
    const user = localStorage.getItem('user');
    const lastActive = localStorage.getItem('lastActive');

    if (token && user) {
      const currentTime = new Date().getTime();
      const lastActiveTime = new Date(lastActive).getTime();
      const sessionTimeout = 24 * 60 * 60 * 1000; // 24 hours

      if (currentTime - lastActiveTime < sessionTimeout) {
        navigate('/');
      } else {
        localStorage.clear();
      }
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
      timestamp: getCurrentDateTime(),
      lastActive: getCurrentDateTime()
    }));
    setErrors(prev => ({
      ...prev,
      [name]: '',
      general: ''
    }));
  };

  const validate = () => {
    const newErrors = {};
    if (!formData.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = "Email is invalid";
    }

    if (!formData.password.trim()) {
      newErrors.password = "Password is required";
    } else if (formData.password.length < 8) {
      newErrors.password = "Password must be at least 8 characters";
    }

    setErrors(prev => ({
      ...prev,
      ...newErrors
    }));

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validate()) return;

    try {
      setLoading(true);
      const loginData = {
        ...formData,
        timestamp: getCurrentDateTime(),
        lastActive: getCurrentDateTime()
      };

      const response = await loginApi(loginData);

      if (response.data.success) {
        const userData = {
          ...response.data.data.user,
          lastActive: getCurrentDateTime(),
          loginTime: getCurrentDateTime()
        };

        localStorage.setItem('token', response.data.data.token);
        localStorage.setItem('user', JSON.stringify(userData));
        localStorage.setItem('lastActive', getCurrentDateTime());

        // Log successful login
        console.log('Login successful:', {
          user: userData.email,
          timestamp: getCurrentDateTime(),
          sessionId: response.data.data.sessionId || 'N/A'
        });

        onLogin();
        navigate('/');
      }
    } catch (error) {
      console.error('Login error:', {
        error: error.message,
        timestamp: getCurrentDateTime(),
        email: formData.email
      });

      const errorMessage = error.response?.data?.message || 'Login failed. Please try again.';
      toast.error(errorMessage);
      setErrors(prev => ({
        ...prev,
        general: errorMessage
      }));
    } finally {
      setLoading(false);
    }
  };

  const handleRememberMe = (e) => {
    if (e.target.checked) {
      localStorage.setItem('rememberedEmail', formData.email);
    } else {
      localStorage.removeItem('rememberedEmail');
    }
  };

  useEffect(() => {
    const rememberedEmail = localStorage.getItem('rememberedEmail');
    if (rememberedEmail) {
      setFormData(prev => ({
        ...prev,
        email: rememberedEmail
      }));
    }
  }, []);

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h1>Welcome Back</h1>
          <p>Please sign in to continue</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {errors.general && (
            <div className="error-message">
              <i className="fas fa-exclamation-circle"></i>
              <span>{errors.general}</span>
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">
              <i className="fas fa-envelope"></i>
              Email
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder="Enter your email"
              className={`form-input ${errors.email ? 'error' : ''}`}
              autoComplete="email"
            />
            {errors.email && <span className="error-text">{errors.email}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="password">
              <i className="fas fa-lock"></i>
              Password
            </label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder="Enter your password"
              className={`form-input ${errors.password ? 'error' : ''}`}
              autoComplete="current-password"
            />
            {errors.password && <span className="error-text">{errors.password}</span>}
          </div>

          <div className="form-options">
            <label className="remember-me">
              <input 
                type="checkbox" 
                onChange={handleRememberMe}
                defaultChecked={!!localStorage.getItem('rememberedEmail')}
              />
              <span>Remember me</span>
            </label>
            <Link to="/forgot-password" className="forgot-password">
              Forgot Password?
            </Link>
          </div>

          <button 
            type="submit" 
            className={`login-button ${loading ? 'loading' : ''}`}
            disabled={loading}
          >
            {loading ? (
              <LoadingSpinner fullScreen={false} />
            ) : (
              'Sign In'
            )}
          </button>
        </form>

        <div className="login-footer">
          <p>Don't have an account? {' '}
            <Link to="/register" className="signup-link">
              Sign up
            </Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Loginpage;
