import React, { useState, useEffect, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import LoadingSpinner from './components/LoadingSpinner/LoadingSpinner';
import SessionManager from './components/SessionManager/SessionManager';
import { isLoggedIn, getStoredUserData } from './apis/Api';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// Utility Functions
const getCurrentUTCDateTime = () => {
    const now = new Date();
    const year = now.getUTCFullYear();
    const month = String(now.getUTCMonth() + 1).padStart(2, '0');
    const day = String(now.getUTCDate()).padStart(2, '0');
    const hours = String(now.getUTCHours()).padStart(2, '0');
    const minutes = String(now.getUTCMinutes()).padStart(2, '0');
    const seconds = String(now.getUTCSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
};

// System configuration
const SYSTEM_CONFIG = {
    SESSION_CHECK_INTERVAL: 10000,
    SESSION_TIMEOUT: 3600000,
    WARNING_TIME: 3300000,
    DEFAULT_USER: 'raj2080'
};

// Lazy load components
const lazyComponents = {
    Homepage: React.lazy(() => import('./pages/homepage/Homepage')),
    Loginpage: React.lazy(() => import('./pages/loginpage/Loginpage')),
    Registerpage: React.lazy(() => import('./pages/registerpage/Registerpage')),
    Profilepage: React.lazy(() => import('./pages/profile/Profilepage')),
    Communities: React.lazy(() => import('./pages/communities/Communities')),
    ContactUs: React.lazy(() => import('./pages/contactus/ContactUs')),
    CreateConfession: React.lazy(() => import('./pages/confession/CreateConfession')),
    EditConfession: React.lazy(() => import('./pages/confession/EditConfession')),
    Forgetpassword: React.lazy(() => import('./pages/forgetpass/Forgetpassword')),
    ResetPassword: React.lazy(() => import('./pages/forgetpass/Resetpassword'))
};

// Protected Route Component
const ProtectedRoute = ({ children }) => {
    const location = useLocation();
    const authenticated = isLoggedIn();
    const currentTime = getCurrentUTCDateTime();

    useEffect(() => {
        if (!authenticated) {
            console.log('Authentication check failed:', {
                timestamp: currentTime,
                path: location.pathname,
                user: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')).nickname : SYSTEM_CONFIG.DEFAULT_USER
            });
        }
    }, [authenticated, location, currentTime]);

    if (!authenticated) {
        return <Navigate to="/login" state={{ from: location.pathname }} replace />;
    }

    return children;
};

// Public Route Component
const PublicRoute = ({ children }) => {
    const location = useLocation();
    const authenticated = isLoggedIn();
    const currentTime = getCurrentUTCDateTime();

    if (authenticated) {
        console.log('Redirecting authenticated user:', {
            timestamp: currentTime,
            destination: location.state?.from || '/',
            user: getStoredUserData()?.nickname || SYSTEM_CONFIG.DEFAULT_USER
        });
        return <Navigate to={location.state?.from || '/'} replace />;
    }

    return children;
};

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(isLoggedIn());
    const [isLoading, setIsLoading] = useState(true);
    const [currentUser, setCurrentUser] = useState(getStoredUserData());
    const [lastActive, setLastActive] = useState(getCurrentUTCDateTime());

    useEffect(() => {
        const checkAuthStatus = () => {
            const currentTime = getCurrentUTCDateTime();
            const authenticated = isLoggedIn();
            setIsAuthenticated(authenticated);

            if (authenticated) {
                const userData = getStoredUserData();
                setCurrentUser(userData);
                setLastActive(currentTime);

                console.log('Session active:', {
                    timestamp: currentTime,
                    user: userData?.nickname || SYSTEM_CONFIG.DEFAULT_USER,
                    lastActive: currentTime
                });
            } else {
                setCurrentUser(null);
            }

            setIsLoading(false);
        };

        // Activity tracking
        const activityEvents = ['mousemove', 'keydown', 'click', 'scroll', 'touchstart'];
        const handleActivity = () => {
            const currentTime = getCurrentUTCDateTime();
            setLastActive(currentTime);
            localStorage.setItem('lastActive', currentTime);
        };

        activityEvents.forEach(event => {
            window.addEventListener(event, handleActivity);
        });

        checkAuthStatus();
        const authCheckInterval = setInterval(checkAuthStatus, SYSTEM_CONFIG.SESSION_CHECK_INTERVAL);

        return () => {
            activityEvents.forEach(event => {
                window.removeEventListener(event, handleActivity);
            });
            clearInterval(authCheckInterval);
        };
    }, []);

    const handleLogin = () => {
        const currentTime = getCurrentUTCDateTime();
        setIsAuthenticated(true);
        const userData = getStoredUserData();
        setCurrentUser(userData);
        setLastActive(currentTime);

        console.log('Login successful:', {
            timestamp: currentTime,
            user: userData?.nickname || SYSTEM_CONFIG.DEFAULT_USER
        });
    };

    const handleLogout = () => {
        const currentTime = getCurrentUTCDateTime();

        console.log('Logout initiated:', {
            timestamp: currentTime,
            user: currentUser?.nickname || SYSTEM_CONFIG.DEFAULT_USER
        });

        localStorage.clear();
        sessionStorage.clear();
        setIsAuthenticated(false);
        setCurrentUser(null);
    };

    if (isLoading) {
        return <LoadingSpinner />;
    }

    return (
        <Router>
            <div className="app-container">
                <SessionManager 
                    lastActive={lastActive}
                    currentUser={currentUser}
                    onSessionExpired={handleLogout}
                />

                <Navbar 
                    isAuthenticated={isAuthenticated} 
                    onLogout={handleLogout}
                    user={currentUser}
                />

                <main className="main-content">
                    <Suspense fallback={<LoadingSpinner />}>
                        <Routes>
                            {/* Public Routes */}
                            <Route path="/" element={<lazyComponents.Homepage />} />
                            <Route path="/communities" element={<lazyComponents.Communities />} />
                            <Route path="/contact" element={<lazyComponents.ContactUs />} />

                            {/* Auth Routes */}
                            <Route
                                path="/login"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.Loginpage onLogin={handleLogin} />
                                    </PublicRoute>
                                }
                            />
                            <Route
                                path="/register"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.Registerpage />
                                    </PublicRoute>
                                }
                            />
                            <Route
                                path="/forgot-password"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.Forgetpassword />
                                    </PublicRoute>
                                }
                            />
                            <Route
                                path="/reset-password/:token"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.ResetPassword />
                                    </PublicRoute>
                                }
                            />

                            {/* Protected Routes */}
                            <Route
                                path="/profile"
                                element={
                                    <ProtectedRoute>
                                        <lazyComponents.Profilepage user={currentUser} />
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/create-confession"
                                element={
                                    <ProtectedRoute>
                                        <lazyComponents.CreateConfession user={currentUser} />
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/edit-confession/:id"
                                element={
                                    <ProtectedRoute>
                                        <lazyComponents.EditConfession user={currentUser} />
                                    </ProtectedRoute>
                                }
                            />

                            {/* 404 Route */}
                            <Route
                                path="*"
                                element={
                                    <div className="error-page">
                                        <h1>404 - Page Not Found</h1>
                                        <p>Last checked: {getCurrentUTCDateTime()}</p>
                                    </div>
                                }
                            />
                        </Routes>
                    </Suspense>
                </main>

                <Footer timestamp={getCurrentUTCDateTime()} />

                <ToastContainer
                    position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover
                    theme="colored"
                />
            </div>
        </Router>
    );
}

export default App;
