import React, { useEffect, useState } from 'react';
import { 
  getUserProfile, 
  updateUserProfile, 
  changePassword,
  getUserConfessions,
  deleteConfession,
  getStoredUserData
} from '../../apis/Api';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './Profilepage.css';

const Profilepage = () => {
  // State Management
  const [activeView, setActiveView] = useState('profile');
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [currentDateTime, setCurrentDateTime] = useState('');
  const [userConfessions, setUserConfessions] = useState([]);
  const [showPasswordSection, setShowPasswordSection] = useState(false);
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [passwordSuccess, setPasswordSuccess] = useState('');
  const [passwordError, setPasswordError] = useState('');

  // Form States
  const [formData, setFormData] = useState({
    nickname: '',
    email: ''
  });

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const navigate = useNavigate();

  // Initialize data and setup timers
  useEffect(() => {
    const fetchData = async () => {
      try {
        const storedUser = getStoredUserData();
        if (!storedUser) {
          navigate('/login');
          return;
        }

        const [profileRes, confessionsRes] = await Promise.all([
          getUserProfile(),
          getUserConfessions()
        ]);

        if (profileRes.data?.success && profileRes.data?.data) {
          const profileData = profileRes.data.data;
          setUserData(profileData);
          setFormData({
            nickname: profileData.nickname,
            email: profileData.email
          });
        }

        if (confessionsRes.data?.success) {
          setUserConfessions(confessionsRes.data.confessions);
        }
      } catch (err) {
        toast.error('Failed to load profile data');
        setError('Could not load profile data');
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // Setup datetime update
    const updateDateTime = () => {
      const now = new Date();
      const year = now.getUTCFullYear();
      const month = String(now.getUTCMonth() + 1).padStart(2, '0');
      const day = String(now.getUTCDate()).padStart(2, '0');
      const hours = String(now.getUTCHours()).padStart(2, '0');
      const minutes = String(now.getUTCMinutes()).padStart(2, '0');
      const seconds = String(now.getUTCSeconds()).padStart(2, '0');
      setCurrentDateTime(`${year}-${month}-${day} ${hours}:${minutes}:${seconds}`);
    };

    updateDateTime();
    const timer = setInterval(updateDateTime, 1000);
    return () => clearInterval(timer);
  }, [navigate]);

  // Event Handlers
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCancel = () => {
    setIsEditing(false);
    setFormData({
      nickname: userData?.nickname || '',
      email: userData?.email || ''
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await updateUserProfile(formData);
      
      if (response.data.success) {
        setUserData(response.data.user);
        setIsEditing(false);
        toast.success('Profile updated successfully');
      }
    } catch (err) {
      toast.error('Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setPasswordError('New passwords do not match');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      setPasswordError('New password must be at least 6 characters long');
      return;
    }

    try {
      setPasswordLoading(true);
      const response = await changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      if (response.data.success) {
        setPasswordSuccess('Password changed successfully');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        setTimeout(() => {
          setShowPasswordSection(false);
          setPasswordSuccess('');
        }, 2000);
        toast.success('Password updated successfully');
      }
    } catch (err) {
      setPasswordError('Failed to update password');
      toast.error('Failed to update password');
    } finally {
      setPasswordLoading(false);
    }
  };

  const handleDeleteConfession = async (confessionId) => {
    if (window.confirm('Are you sure you want to delete this confession?')) {
      try {
        await deleteConfession(confessionId);
        setUserConfessions(prev => prev.filter(conf => conf._id !== confessionId));
        toast.success('Confession deleted successfully');
      } catch (err) {
        toast.error('Failed to delete confession');
      }
    }
  };

  // Profile View Component
  const ProfileView = () => (
    <div className="content-card">
      <h2>Profile Settings</h2>
      {isEditing ? (
        <form onSubmit={handleSubmit} className="profile-form">
          <div className="form-group">
            <label>Nickname:</label>
            <input
              type="text"
              name="nickname"
              value={formData.nickname}
              onChange={handleInputChange}
              required
              maxLength="10"
              pattern="^[a-zA-Z0-9_]+$"
              title="Nickname can only contain letters, numbers, and underscores"
              disabled={loading}
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>
          <div className="button-group">
            <button type="submit" className="btn btn-primary" disabled={loading}>
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={handleCancel}
            >
              Cancel
            </button>
          </div>
        </form>
      ) : (
        <>
          <div className="profile-info">
            <div className="info-row">
              <span className="label">Nickname:</span>
              <span className="value">{userData?.nickname}</span>
            </div>
            <div className="info-row">
              <span className="label">Email:</span>
              <span className="value">{userData?.email}</span>
            </div>
            <button 
              className="btn btn-primary mt-2"
              onClick={() => setIsEditing(true)}
            >
              Edit Profile
            </button>
          </div>

          <div className="password-section">
            <h3>Password Settings</h3>
            <button 
              className="btn btn-secondary"
              onClick={() => setShowPasswordSection(!showPasswordSection)}
            >
              {showPasswordSection ? 'Hide Password Form' : 'Change Password'}
            </button>

            {showPasswordSection && (
              <form onSubmit={handlePasswordSubmit} className="password-form mt-2">
                {passwordError && <div className="error-message">{passwordError}</div>}
                {passwordSuccess && <div className="success-message">{passwordSuccess}</div>}
                
                <div className="form-group">
                  <label>Current Password:</label>
                  <input
                    type="password"
                    name="currentPassword"
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>New Password:</label>
                  <input
                    type="password"
                    name="newPassword"
                    value={passwordData.newPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength="6"
                  />
                </div>
                <div className="form-group">
                  <label>Confirm Password:</label>
                  <input
                    type="password"
                    name="confirmPassword"
                    value={passwordData.confirmPassword}
                    onChange={handlePasswordChange}
                    required
                    minLength="6"
                  />
                </div>
                <div className="button-group">
                  <button 
                    type="submit" 
                    className="btn btn-primary"
                    disabled={passwordLoading}
                  >
                    {passwordLoading ? 'Updating...' : 'Update Password'}
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => {
                      setShowPasswordSection(false);
                      setPasswordData({
                        currentPassword: '',
                        newPassword: '',
                        confirmPassword: ''
                      });
                      setPasswordError('');
                    }}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            )}
          </div>
        </>
      )}
    </div>
  );

  // Confessions View Component
  const ConfessionsView = () => (
    <div className="posts-container">
      <h2>Your Confessions</h2>
      {userConfessions.length > 0 ? (
        userConfessions.map(confession => (
          <div key={confession._id} className="post-card">
            <div className="post-header">
              <h3>{confession.title}</h3>
              <span className="post-category">{confession.category}</span>
            </div>
            <p className="post-content">{confession.content}</p>
            <div className="post-footer">
              <div className="post-stats">
                <span>{confession.likes?.length || 0} Likes</span>
                <span>{confession.comments?.length || 0} Comments</span>
              </div>
              <div className="post-actions">
                <button
                  className="btn btn-secondary btn-sm"
                  onClick={() => navigate(`/edit-confession/${confession._id}`)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteConfession(confession._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <div className="no-posts">
          <p>No confessions found</p>
          <button 
            className="btn btn-primary"
            onClick={() => navigate('/new-confession')}
          >
            Create Your First Confession
          </button>
        </div>
      )}
    </div>
  );

  if (loading && !userData) {
    return (
      <div className="profile-page">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="current-info-display">
        <div className="time-display">
          Current Date and Time (UTC): {currentDateTime}
        </div>
        <div className="user-display">
          Current User's Login: {userData?.nickname}
        </div>
      </div>

      <div className="main-layout">
        <div className="main-content">
          {activeView === 'profile' ? <ProfileView /> : <ConfessionsView />}
        </div>

        <div className="right-sidebar">
          <button
            className={`sidebar-button ${activeView === 'profile' ? 'active' : ''}`}
            onClick={() => setActiveView('profile')}
          >
            Profile Settings
          </button>
          <button
            className={`sidebar-button ${activeView === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveView('posts')}
          >
            Your Confessions
          </button>
        </div>
      </div>

      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default Profilepage;
