// src/App.js
import React, { useState, useEffect, Suspense, useCallback, useMemo } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import LoadingSpinner from './components/LoadingSpinner/LoadingSpinner';
import Navbar from './components/Navbar/Navbar';
import Footer from './components/Footer/Footer';
import { isLoggedIn, logout } from './apis/Api';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// Lazy load components
const Homepage = React.lazy(() => import('./pages/homepage/Homepage'));
const Loginpage = React.lazy(() => import('./pages/loginpage/Loginpage'));
const Registerpage = React.lazy(() => import('./pages/registerpage/Registerpage'));
const Profilepage = React.lazy(() => import('./pages/profile/Profilepage'));
const Communities = React.lazy(() => import('./pages/communities/Communities'));
const ContactUs = React.lazy(() => import('./pages/contact/ContactUs'));
const CreateConfession = React.lazy(() => import('./pages/confession/CreateConfession'));

// Protected Route Component
const ProtectedRoute = ({ children }) => {
  const location = useLocation();
  const authenticated = isLoggedIn();

  if (!authenticated) {
    return <Navigate to="/login" state={{ from: location.pathname }} replace />;
  }

  return children;
};

// Public Route Component
const PublicRoute = ({ children }) => {
  const location = useLocation();
  const authenticated = isLoggedIn();

  if (authenticated) {
    return <Navigate to={location.state?.from || '/'} replace />;
  }

  return children;
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(isLoggedIn);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkAuthStatus = () => {
      setIsAuthenticated(isLoggedIn());
      setIsLoading(false);
    };

    checkAuthStatus();
    window.addEventListener('storage', checkAuthStatus);

    return () => {
      window.removeEventListener('storage', checkAuthStatus);
    };
  }, []);

  const handleLogin = () => {
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    logout();
    setIsAuthenticated(false);
  };

  if (isLoading) {
    return <LoadingSpinner />;
  }

  return (
    <Router>
      <div className="app-container">
        <Navbar isAuthenticated={isAuthenticated} onLogout={handleLogout} />

        <main className="main-content">
          <Suspense fallback={<LoadingSpinner />}>
            <Routes>
              {/* Public Routes */}
              <Route path="/" element={<Homepage />} />
              <Route path="/communities" element={<Communities />} />
              <Route path="/contact" element={<ContactUs />} />

              {/* Authentication Routes */}
              <Route
                path="/login"
                element={
                  <PublicRoute>
                    <Loginpage onLogin={handleLogin} />
                  </PublicRoute>
                }
              />
              <Route
                path="/register"
                element={
                  <PublicRoute>
                    <Registerpage />
                  </PublicRoute>
                }
              />

              {/* Protected Routes */}
              <Route
                path="/profile"
                element={
                  <ProtectedRoute>
                    <Profilepage onLogout={handleLogout} />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/create-confession"
                element={
                  <ProtectedRoute>
                    <CreateConfession />
                  </ProtectedRoute>
                }
              />

              {/* 404 Route */}
              <Route
                path="*"
                element={
                  <div className="error-page">
                    <h1>404 - Page Not Found</h1>
                    <p>The page you're looking for doesn't exist.</p>
                    <button onClick={() => window.history.back()}>
                      Go Back
                    </button>
                  </div>
                }
              />
            </Routes>
          </Suspense>
        </main>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
