import React, { useState, useEffect } from 'react';
import { useNavigate, Link, useLocation } from 'react-router-dom';
import { loginApi } from "../../apis/Api";
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/LoadingSpinner/LoadingSpinner';
import './Loginpage.css';

// Storage Keys
const STORAGE_KEYS = {
    REMEMBERED_EMAIL: 'rememberedEmail',
    SESSION_WARNING: 'sessionWarningShown',
    TOKEN: 'token',
    USER: 'user',
    LAST_ACTIVE: 'lastActive',
    SESSION_START: 'sessionStart',
    SESSION_ID: 'sessionId'
};

// Utility Functions
const getCurrentUTCDateTime = () => {
    const now = new Date();
    return now.toISOString()
        .replace('T', ' ')
        .replace(/\.\d+Z$/, '');
};

const getSystemInfo = () => ({
    userAgent: navigator.userAgent,
    platform: navigator.platform,
    language: navigator.language,
    timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    screenResolution: `${window.screen.width}x${window.screen.height}`,
    timestamp: getCurrentUTCDateTime()
});

const Loginpage = ({ onLogin }) => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        timestamp: getCurrentUTCDateTime(),
        lastActive: getCurrentUTCDateTime()
    });

    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({
        email: '',
        password: '',
        general: ''
    });

    const navigate = useNavigate();
    const location = useLocation();

    useEffect(() => {
        const initializeLoginPage = () => {
            const currentTime = getCurrentUTCDateTime();

            // Clear existing session data except remembered email
            Object.keys(localStorage).forEach(key => {
                if (key !== STORAGE_KEYS.REMEMBERED_EMAIL) {
                    localStorage.removeItem(key);
                }
            });

            // Check for session expiry redirect
            const searchParams = new URLSearchParams(location.search);
            if (searchParams.get('session') === 'expired') {
                toast.info('Your session has expired. Please login again.', {
                    toastId: 'session-expired'
                });
            }

            // Check for remembered email
            const rememberedEmail = localStorage.getItem(STORAGE_KEYS.REMEMBERED_EMAIL);
            if (rememberedEmail) {
                setFormData(prev => ({
                    ...prev,
                    email: rememberedEmail,
                    timestamp: currentTime,
                    lastActive: currentTime
                }));
            }

            // Log initialization
            console.log('Login page initialized:', {
                timestamp: currentTime,
                rememberedEmail: !!rememberedEmail,
                sessionExpired: searchParams.get('session') === 'expired',
                systemInfo: getSystemInfo()
            });

            document.title = `Login - Confession App (${currentTime})`;
        };

        initializeLoginPage();

        return () => {
            toast.dismiss('session-expired');
        };
    }, [location.search]);

    const validateForm = () => {
        const newErrors = {};
        const currentTime = getCurrentUTCDateTime();

        if (!formData.email) {
            newErrors.email = "Email is required";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "Please enter a valid email address";
        }

        if (!formData.password) {
            newErrors.password = "Password is required";
        } else if (formData.password.length < 8) {
            newErrors.password = "Password must be at least 8 characters";
        }

        setErrors(prev => ({
            ...prev,
            ...newErrors
        }));

        console.log('Form validation:', {
            timestamp: currentTime,
            hasErrors: Object.keys(newErrors).length > 0,
            fields: Object.keys(formData)
        });

        return Object.keys(newErrors).length === 0;
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        const currentTime = getCurrentUTCDateTime();

        setFormData(prev => ({
            ...prev,
            [name]: value,
            timestamp: currentTime,
            lastActive: currentTime
        }));

        setErrors(prev => ({
            ...prev,
            [name]: '',
            general: ''
        }));

        localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, currentTime);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) return;

        const currentTime = getCurrentUTCDateTime();

        try {
            setLoading(true);
            console.log('Login attempt:', {
                email: formData.email,
                timestamp: currentTime,
                systemInfo: getSystemInfo()
            });
            
            const loginData = {
                email: formData.email,
                password: formData.password,
                timestamp: currentTime,
                lastActive: currentTime,
                systemInfo: getSystemInfo()
            };

            const response = await loginApi(loginData);

            if (response.data?.success) {
                const { user, token, sessionId } = response.data.data;

                // Store session data
                localStorage.setItem(STORAGE_KEYS.TOKEN, token);
                localStorage.setItem(STORAGE_KEYS.SESSION_ID, sessionId);
                localStorage.setItem(STORAGE_KEYS.SESSION_START, currentTime);
                localStorage.setItem(STORAGE_KEYS.LAST_ACTIVE, currentTime);
                localStorage.setItem(STORAGE_KEYS.USER, JSON.stringify({
                    ...user,
                    lastLoginTime: currentTime,
                    lastActive: currentTime,
                    systemInfo: getSystemInfo()
                }));

                // Handle remember me
                const rememberMe = document.getElementById('rememberMe').checked;
                if (rememberMe) {
                    localStorage.setItem(STORAGE_KEYS.REMEMBERED_EMAIL, formData.email);
                } else {
                    localStorage.removeItem(STORAGE_KEYS.REMEMBERED_EMAIL);
                }

                console.log('Login successful:', {
                    userId: user._id,
                    nickname: user.nickname,
                    sessionId,
                    timestamp: currentTime
                });

                toast.success(`Welcome back, ${user.nickname}!`);
                onLogin();
                navigate(location.state?.from || '/');
            }
        } catch (error) {
            console.error('Login error:', {
                error: error.message,
                timestamp: currentTime,
                systemInfo: getSystemInfo()
            });
            
            setErrors(prev => ({
                ...prev,
                general: error.message || 'Login failed. Please try again.'
            }));
            
            toast.error(error.message || 'Login failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <div className="login-header">
                    <h1>Welcome Back</h1>
                    <p>Please sign in to continue</p>
                    <p className="timestamp">Last Updated: {formData.timestamp}</p>
                </div>

                <form onSubmit={handleSubmit} className="login-form">
                    {errors.general && (
                        <div className="error-message" role="alert">
                            <i className="fas fa-exclamation-circle" aria-hidden="true"></i>
                            <span>{errors.general}</span>
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="email">
                            <i className="fas fa-envelope" aria-hidden="true"></i>
                            Email
                        </label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            placeholder="Enter your email"
                            className={`form-input ${errors.email ? 'error' : ''}`}
                            autoComplete="email"
                            aria-describedby={errors.email ? "emailError" : undefined}
                            required
                        />
                        {errors.email && (
                            <span className="error-text" id="emailError" role="alert">
                                {errors.email}
                            </span>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">
                            <i className="fas fa-lock" aria-hidden="true"></i>
                            Password
                        </label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            placeholder="Enter your password"
                            className={`form-input ${errors.password ? 'error' : ''}`}
                            autoComplete="current-password"
                            aria-describedby={errors.password ? "passwordError" : undefined}
                            required
                            minLength="8"
                        />
                        {errors.password && (
                            <span className="error-text" id="passwordError" role="alert">
                                {errors.password}
                            </span>
                        )}
                    </div>

                    <div className="form-options">
                        <label className="remember-me">
                            <input
                                type="checkbox"
                                id="rememberMe"
                                defaultChecked={!!localStorage.getItem(STORAGE_KEYS.REMEMBERED_EMAIL)}
                            />
                            <span>Remember me</span>
                        </label>
                        <Link to="/forgot-password" className="forgot-password">
                            Forgot Password?
                        </Link>
                    </div>

                    <button 
                        type="submit" 
                        className={`login-button ${loading ? 'loading' : ''}`}
                        disabled={loading}
                        aria-busy={loading}
                    >
                        {loading ? (
                            <LoadingSpinner fullScreen={false} />
                        ) : (
                            'Sign In'
                        )}
                    </button>
                </form>

                <div className="login-footer">
                    <p>
                        Don't have an account?{' '}
                        <Link to="/register" className="signup-link">
                            Sign up
                        </Link>
                    </p>
                    <p className="session-info">
                        Session started at: {getCurrentUTCDateTime()}
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Loginpage;
