import React, { useState } from 'react';
import { updateConfession } from '../../apis/Api';
import './ConfessionsView.css'; // Ensure the path is correct

const ConfessionsView = ({ confessions, loading, userData, currentDateTime }) => {
  const [editingConfession, setEditingConfession] = useState(null);
  const [editContent, setEditContent] = useState({
    content: '',
    title: '',
    category: '',
    imageUrl: ''
  });

  const handleEditClick = (confession) => {
    setEditingConfession(confession._id);
    setEditContent({
      content: confession.content,
      title: confession.title,
      category: confession.category,
      imageUrl: confession.imageUrl
    });
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditContent((prevContent) => ({
      ...prevContent,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setEditContent((prevContent) => ({
        ...prevContent,
        imageUrl: URL.createObjectURL(file)
      }));
    }
  };

  const handleEditSubmit = async (confessionId) => {
    try {
      // Update the API call according to your backend requirements to handle file upload
      await updateConfession(confessionId, editContent);
      setEditingConfession(null);
      // Optionally, refetch the confessions to get the updated content
      // fetchConfessions();
    } catch (error) {
      console.error('Failed to update confession:', error);
    }
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner"></div>
        <p>Loading confessions...</p>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <div className="header-content">
          <h1>Your Confessions</h1>
          <div className="current-info">
            <p className="current-user">Current User's Login: {userData?.nickname}</p>
            <p className="current-datetime">Current Date and Time (UTC - YYYY-MM-DD HH:MM:SS formatted): {currentDateTime}</p>
          </div>
        </div>
      </div>

      <div className="confessions-list">
        {confessions && confessions.length > 0 ? (
          confessions.map((confession) => (
            <div key={confession._id} className="confession-card">
              {editingConfession === confession._id ? (
                <div>
                  <input
                    type="text"
                    name="title"
                    value={editContent.title}
                    onChange={handleEditChange}
                    className="edit-input"
                    placeholder="Title"
                  />
                  <textarea
                    name="content"
                    value={editContent.content}
                    onChange={handleEditChange}
                    className="edit-textarea"
                    placeholder="Content"
                  />
                  <input
                    type="text"
                    name="category"
                    value={editContent.category}
                    onChange={handleEditChange}
                    className="edit-input"
                    placeholder="Category"
                  />
                  <input
                    type="file"
                    name="image"
                    onChange={handleImageChange}
                    className="edit-input"
                  />
                  {editContent.imageUrl && (
                    <img
                      src={editContent.imageUrl}
                      alt="Confession"
                      className="confession-image-preview"
                    />
                  )}
                  <button
                    onClick={() => handleEditSubmit(confession._id)}
                    className="save-button"
                  >
                    Save
                  </button>
                  <button
                    onClick={() => setEditingConfession(null)}
                    className="cancel-button"
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <>
                  <h3 className="confession-title">{confession.title}</h3>
                  <p className="confession-text">{confession.content}</p>
                  {confession.imageUrl && (
                    <img
                      src={`http://localhost:5000/${confession.imageUrl.replace(/^C:\\uiux development\\backend\\/, '')}`}
                      alt="Confession"
                      className="confession-image"
                    />
                  )}
                  <span className="confession-category">Category: {confession.category}</span>
                </>
              )}
              <div className="confession-meta">
                <span className="confession-date">
                  Posted: {new Date(confession.createdAt).toLocaleString()}
                </span>
              </div>
              {editingConfession !== confession._id && (
                <button
                  onClick={() => handleEditClick(confession)}
                  className="edit-button"
                >
                  Edit
                </button>
              )}
            </div>
          ))
        ) : (
          <p className="no-confessions">No confessions yet.</p>
        )}
      </div>
    </div>
  );
};

export default ConfessionsView;
