import React, { useState, useEffect, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import LoadingSpinner from './components/LoadingSpinner/LoadingSpinner';
import SessionManager from './components/SessionManager/SessionManager';
import { isLoggedIn, getStoredUserData } from './apis/Api';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';

// Utility Functions
const getCurrentUTCDateTime = () => {
    const now = new Date();
    return now.toISOString()
        .replace('T', ' ')      // Replace T with space
        .replace(/\.\d+Z$/, ''); // Remove milliseconds and Z
};

const getSystemInfo = () => ({
    userAgent: navigator.userAgent,
    platform: navigator.platform,
    language: navigator.language,
    timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
    screenResolution: `${window.screen.width}x${window.screen.height}`,
    timestamp: getCurrentUTCDateTime()
});

// System configuration with dynamic values
const getSystemConfig = () => ({
    SESSION_CHECK_INTERVAL: parseInt(process.env.REACT_APP_SESSION_CHECK_INTERVAL) || 10000,
    SESSION_TIMEOUT: parseInt(process.env.REACT_APP_SESSION_TIMEOUT) || 3600000,
    WARNING_TIME: parseInt(process.env.REACT_APP_WARNING_TIME) || 3300000
});

// Lazy load components
const componentImports = {
    Homepage: () => import('./pages/homepage/Homepage'),
    Loginpage: () => import('./pages/loginpage/Loginpage'),
    Registerpage: () => import('./pages/registerpage/Registerpage'),
    Profilepage: () => import('./pages/profile/Profilepage'),
    Communities: () => import('./pages/communities/Communities'),
    ContactUs: () => import('./pages/contactus/ContactUs'),
    CreateConfession: () => import('./pages/confession/CreateConfession'),
    EditConfession: () => import('./pages/confession/EditConfession'),
    Forgetpassword: () => import('./pages/forgetpass/Forgetpassword'),
    ResetPassword: () => import('./pages/forgetpass/Resetpassword')
};

// Create lazy components
const lazyComponents = Object.fromEntries(
    Object.entries(componentImports).map(([key, importFn]) => [
        key,
        React.lazy(importFn)
    ])
);

// Protected Route Component
const ProtectedRoute = ({ children }) => {
    const location = useLocation();
    const authenticated = isLoggedIn();
    const currentTime = getCurrentUTCDateTime();

    useEffect(() => {
        if (!authenticated) {
            console.log('Authentication failed:', {
                timestamp: currentTime,
                path: location.pathname,
                lastActive: localStorage.getItem('lastActive')
            });
            localStorage.setItem('redirectUrl', location.pathname);
            localStorage.setItem('lastFailedAuth', currentTime);
        }
    }, [authenticated, location, currentTime]);

    if (!authenticated) {
        return <Navigate to="/login" state={{ from: location.pathname, timestamp: currentTime }} replace />;
    }

    return children;
};

// Public Route Component
const PublicRoute = ({ children }) => {
    const location = useLocation();
    const authenticated = isLoggedIn();
    const currentTime = getCurrentUTCDateTime();

    if (authenticated) {
        const redirectUrl = localStorage.getItem('redirectUrl') || '/';
        localStorage.removeItem('redirectUrl');
        localStorage.setItem('lastRedirect', currentTime);
        return <Navigate to={redirectUrl} replace />;
    }

    return children;
};

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(isLoggedIn);
    const [isLoading, setIsLoading] = useState(true);
    const [currentUser, setCurrentUser] = useState(getStoredUserData);
    const [lastActive, setLastActive] = useState(getCurrentUTCDateTime);
    const [systemConfig] = useState(getSystemConfig);

    useEffect(() => {
        const checkAuthStatus = () => {
            const currentTime = getCurrentUTCDateTime();
            const authenticated = isLoggedIn();
            setIsAuthenticated(authenticated);

            if (authenticated) {
                const userData = getStoredUserData();
                setCurrentUser(userData);
                localStorage.setItem('lastActive', currentTime);
                setLastActive(currentTime);

                console.log('Session active:', {
                    user: userData?.nickname || 'raj2080',
                    timestamp: currentTime,
                    lastActive: currentTime
                });
            } else {
                setCurrentUser(null);
            }

            setIsLoading(false);
        };

        const activityEvents = ['mousemove', 'keydown', 'click', 'scroll', 'touchstart'];
        const handleActivity = () => {
            const currentTime = getCurrentUTCDateTime();
            setLastActive(currentTime);
            localStorage.setItem('lastActive', currentTime);
        };

        // Add event listeners
        activityEvents.forEach(event => {
            window.addEventListener(event, handleActivity);
        });

        checkAuthStatus();

        const authCheckInterval = setInterval(checkAuthStatus, systemConfig.SESSION_CHECK_INTERVAL);
        window.addEventListener('storage', checkAuthStatus);

        // Cleanup
        return () => {
            activityEvents.forEach(event => {
                window.removeEventListener(event, handleActivity);
            });
            window.removeEventListener('storage', checkAuthStatus);
            clearInterval(authCheckInterval);
        };
    }, [systemConfig.SESSION_CHECK_INTERVAL]);

    const handleLogin = () => {
        const currentTime = getCurrentUTCDateTime();

        console.log('HandleLogin called:', {
            timestamp: currentTime,
            status: 'attempting login',
            environment: process.env.NODE_ENV,
            sessionInfo: {
                lastActive: localStorage.getItem('lastActive'),
                sessionStart: currentTime
            }
        });

        try {
            setIsAuthenticated(true);
            const userData = getStoredUserData();

            console.log('User data retrieved:', {
                userId: userData?._id,
                nickname: userData?.nickname || 'raj2080',
                timestamp: currentTime,
                systemInfo: getSystemInfo()
            });

            setCurrentUser(userData);
            setLastActive(currentTime);

            // Update session information
            localStorage.setItem('lastLogin', currentTime);
            localStorage.setItem('lastActive', currentTime);
            localStorage.setItem('sessionStart', currentTime);

            const sessionId = Math.random().toString(36).substring(7);
            const sessionData = {
                startTime: currentTime,
                lastActive: currentTime,
                user: userData?.nickname || 'raj2080',
                timestamp: currentTime,
                sessionId: sessionId
            };

            localStorage.setItem('sessionId', sessionId);
            localStorage.setItem('sessionData', JSON.stringify(sessionData));

            console.log('Login process completed:', {
                timestamp: currentTime,
                status: 'success',
                user: userData?.nickname || 'raj2080',
                sessionId: sessionId
            });

        } catch (error) {
            console.error('Login process error:', {
                timestamp: currentTime,
                error: error.message,
                stack: error.stack
            });

            setIsAuthenticated(false);
            setCurrentUser(null);
            throw error;
        }
    };

    const handleLogout = () => {
        const currentTime = getCurrentUTCDateTime();

        console.log('Logout initiated:', {
            timestamp: currentTime,
            user: currentUser?.nickname || 'raj2080',
            sessionInfo: {
                lastActive: lastActive,
                sessionDuration: localStorage.getItem('sessionStart') ? 
                    new Date(currentTime) - new Date(localStorage.getItem('sessionStart')) : 
                    null
            }
        });

        // Clear storage and reset state
        Object.keys(localStorage).forEach(key => localStorage.removeItem(key));
        sessionStorage.clear();
        setIsAuthenticated(false);
        setCurrentUser(null);
        localStorage.setItem('lastLogout', currentTime);
    };

    if (isLoading) {
        return <LoadingSpinner fullScreen />;
    }

    return (
        <Router>
            <div className="app-container">
                <SessionManager 
                    lastActive={lastActive}
                    currentUser={currentUser}
                    onSessionExpired={handleLogout}
                />

                <Navbar 
                    isAuthenticated={isAuthenticated} 
                    onLogout={handleLogout}
                    user={currentUser}
                    lastActive={lastActive}
                />

                <main className="main-content">
                    <Suspense fallback={<LoadingSpinner />}>
                        <Routes>
                            {/* Public Routes */}
                            <Route path="/" element={<lazyComponents.Homepage />} />
                            <Route path="/communities" element={<lazyComponents.Communities />} />
                            <Route path="/contact" element={<lazyComponents.ContactUs />} />

                            {/* Auth Routes */}
                            <Route
                                path="/login"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.Loginpage onLogin={handleLogin} />
                                    </PublicRoute>
                                }
                            />
                            <Route
                                path="/register"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.Registerpage />
                                    </PublicRoute>
                                }
                            />
                            <Route
                                path="/forgot-password"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.Forgetpassword />
                                    </PublicRoute>
                                }
                            />
                            <Route
                                path="/reset-password/:token"
                                element={
                                    <PublicRoute>
                                        <lazyComponents.ResetPassword />
                                    </PublicRoute>
                                }
                            />

                            {/* Protected Routes */}
                            <Route
                                path="/profile"
                                element={
                                    <ProtectedRoute>
                                        <lazyComponents.Profilepage 
                                            onLogout={handleLogout} 
                                            user={currentUser}
                                            lastActive={lastActive}
                                        />
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/create-confession"
                                element={
                                    <ProtectedRoute>
                                        <lazyComponents.CreateConfession 
                                            user={currentUser}
                                            timestamp={getCurrentUTCDateTime()}
                                        />
                                    </ProtectedRoute>
                                }
                            />
                            <Route
                                path="/edit-confession/:id"
                                element={
                                    <ProtectedRoute>
                                        <lazyComponents.EditConfession 
                                            user={currentUser}
                                            timestamp={getCurrentUTCDateTime()}
                                        />
                                    </ProtectedRoute>
                                }
                            />

                            {/* Confession Routes */}
                            <Route
                                path="/confessions"
                                element={
                                    <Suspense fallback={<LoadingSpinner />}>
                                        <lazyComponents.Homepage />
                                    </Suspense>
                                }
                            />

                            {/* 404 Route */}
                            <Route
                                path="*"
                                element={
                                    <div className="error-page">
                                        <h1>404 - Page Not Found</h1>
                                        <p>The page you're looking for doesn't exist.</p>
                                        <p>Last checked: {getCurrentUTCDateTime()}</p>
                                        <button onClick={() => window.history.back()}>
                                            Go Back
                                        </button>
                                    </div>
                                }
                            />
                        </Routes>
                    </Suspense>
                </main>

                <Footer timestamp={getCurrentUTCDateTime()} />

                <ToastContainer
                    position="top-right"
                    autoClose={5000}
                    hideProgressBar={false}
                    newestOnTop
                    closeOnClick
                    rtl={false}
                    pauseOnFocusLoss
                    draggable
                    pauseOnHover
                    theme="colored"
                />
            </div>
        </Router>
    );
}

export default App;
