import React, { useEffect, useState } from 'react';
import { getUserProfile, updateUserProfile, changePassword, getAllConfessions, dele } from '../../apis/Api';
import { useNavigate } from 'react-router-dom';
import { FaHeart, FaComment, FaEdit, FaTrash, FaEye, FaTags, FaUser, FaEnvelope, FaLock, FaClock } from 'react-icons/fa';

const Profilepage = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [currentDateTime, setCurrentDateTime] = useState('');
  const [formData, setFormData] = useState({
    nickname: '',
    email: ''
  });

  const [showPasswordSection, setShowPasswordSection] = useState(false);
  const [passwordLoading, setPasswordLoading] = useState(false);
  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [passwordError, setPasswordError] = useState('');
  const [passwordSuccess, setPasswordSuccess] = useState('');

  // Post-related states
  const [userPosts, setUserPosts] = useState([]);
  const [postsLoading, setPostsLoading] = useState(true);
  const [postsError, setPostsError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(5);
  const [sortBy, setSortBy] = useState('newest');
  const [filterCategory, setFilterCategory] = useState('all');
  const [selectedPost, setSelectedPost] = useState(null);
  const [showPostModal, setShowPostModal] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem('user'));
        if (!storedUser) {
          throw new Error('User data not found');
        }

        const [profileResponse, postsResponse] = await Promise.all([
          getUserProfile(),
          get()
        ]);

        if (profileResponse.data?.success && profileResponse.data?.data) {
          const profileData = profileResponse.data.data;
          setUserData(profileData);
          setFormData({
            nickname: profileData.nickname,
            email: profileData.email
          });
        }

        if (postsResponse.data?.success) {
          setUserPosts(postsResponse.data.posts);
        }
      } catch (err) {
        console.error('Data fetch error:', err);
        setError('Could not load profile data. Please try again.');
      } finally {
        setLoading(false);
        setPostsLoading(false);
      }
    };

    fetchUserProfile();

    const updateDateTime = () => {
      const now = new Date();
      setCurrentDateTime(now.toISOString().slice(0, 19).replace('T', ' '));
    };

    updateDateTime();
    const timer = setInterval(updateDateTime, 1000);

    return () => clearInterval(timer);
  }, [navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleEdit = () => {
    setIsEditing(true);
    setError(null);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setFormData({
      nickname: userData.nickname,
      email: userData.email
    });
    setError(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      setError(null);

      const response = await updateUserProfile(formData);
      
      if (response.data.success) {
        setUserData(response.data.user);
        setIsEditing(false);
      }
    } catch (err) {
      setError(err.message || 'Failed to update profile. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    setPasswordError('');
    setPasswordSuccess('');

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setPasswordError('New passwords do not match');
      return;
    }

    if (passwordData.newPassword.length < 6) {
      setPasswordError('New password must be at least 6 characters long');
      return;
    }

    try {
      setPasswordLoading(true);
      const response = await changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      if (response.data.success) {
        setPasswordSuccess('Password changed successfully');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          setShowPasswordSection(false);
          setPasswordSuccess('');
        }, 2000);
      }
    } catch (error) {
      setPasswordError(error.message || 'Failed to change password');
    } finally {
      setPasswordLoading(false);
    }
  };

  const handledele = async (postId) => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        await dele(postId);
        setUserPosts(userPosts.filter(post => post.id !== postId));
      } catch (err) {
        console.error('Failed to delete post:', err);
      }
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getSortedAndFilteredPosts = () => {
    let filteredPosts = [...userPosts];

    if (filterCategory !== 'all') {
      filteredPosts = filteredPosts.filter(post => post.category === filterCategory);
    }

    switch (sortBy) {
      case 'newest':
        return filteredPosts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      case 'oldest':
        return filteredPosts.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
      case 'mostLiked':
        return filteredPosts.sort((a, b) => b.likes - a.likes);
      case 'mostCommented':
        return filteredPosts.sort((a, b) => b.comments - a.comments);
      default:
        return filteredPosts;
    }
  };

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = getSortedAndFilteredPosts().slice(indexOfFirstPost, indexOfLastPost);
  const totalPages = Math.ceil(getSortedAndFilteredPosts().length / postsPerPage);

  if (loading && !userData) {
    return (
      <div className="profile-page">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading profile...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-layout">
        {/* Left Section - Posts */}
        <div className="posts-section">
          <div className="posts-header">
            <h2>Your Posts</h2>
            <div className="posts-controls">
              <select 
                value={sortBy} 
                onChange={(e) => setSortBy(e.target.value)}
                className="sort-select"
              >
                <option value="newest">Newest First</option>
                <option value="oldest">Oldest First</option>
                <option value="mostLiked">Most Liked</option>
                <option value="mostCommented">Most Commented</option>
              </select>

              <select 
                value={filterCategory} 
                onChange={(e) => setFilterCategory(e.target.value)}
                className="category-select"
              >
                <option value="all">All Categories</option>
                <option value="technology">Technology</option>
                <option value="lifestyle">Lifestyle</option>
                <option value="travel">Travel</option>
                <option value="food">Food</option>
              </select>
            </div>
          </div>

          {postsLoading ? (
            <div className="loading-spinner">
              <div className="spinner"></div>
              <p>Loading posts...</p>
            </div>
          ) : (
            <div className="posts-container">
              {currentPosts.map(post => (
                <div key={post.id} className="post-card">
                  <div className="post-card-header">
                    <div className="post-category">
                      <FaTags />
                      <span>{post.category}</span>
                    </div>
                    <div className="post-actions">
                      <button onClick={() => setSelectedPost(post)}>
                        <FaEye />
                      </button>
                      <button onClick={() => navigate(`/edit-post/${post.id}`)}>
                        <FaEdit />
                      </button>
                      <button onClick={() => handledele(post.id)}>
                        <FaTrash />
                      </button>
                    </div>
                  </div>
                  <h3>{post.title}</h3>
                  <p>{post.content.substring(0, 150)}...</p>
                  <div className="post-metadata">
                    <div className="post-stats">
                      <span><FaHeart /> {post.likes}</span>
                      <span><FaComment /> {post.comments}</span>
                    </div>
                    <div className="post-date">{formatDate(post.createdAt)}</div>
                  </div>
                </div>
              ))}

              {currentPosts.length === 0 && (
                <p className="no-posts">No posts found.</p>
              )}

              {totalPages > 1 && (
                <div className="pagination">
                  <button 
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    disabled={currentPage === 1}
                  >
                    Previous
                  </button>
                  <span>Page {currentPage} of {totalPages}</span>
                  <button 
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                    disabled={currentPage === totalPages}
                  >
                    Next
                  </button>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Right Section - Profile Settings */}
        <div className="settings-section">
          <div className="profile-header">
            <h2>Profile Settings</h2>
            <div className="current-info">
              <p><FaUser /> Current User: {userData?.nickname}</p>
              <p><FaClock /> {currentDateTime}</p>
            </div>
          </div>

          {error && (
            <div className="error-message">
              <p>{error}</p>
            </div>
          )}

          <div className="profile-form-container">
            {isEditing ? (
              <form onSubmit={handleSubmit} className="profile-form">
                <div className="form-group">
                  <label>
                    <FaUser />
                    Nickname
                  </label>
                  <input
                    type="text"
                    name="nickname"
                    value={formData.nickname}
                    onChange={handleInputChange}
                    required
                    maxLength="10"
                  />
                </div>

                <div className="form-group">
                  <label>
                    <FaEnvelope />
                    Email
                  </label>
                  <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                  />
                </div>

                <div className="form-actions">
                  <button type="submit" disabled={loading}>
                    {loading ? 'Saving...' : 'Save Changes'}
                  </button>
                  <button type="button" onClick={handleCancel}>
                    Cancel
                  </button>
                </div>
              </form>
            ) : (
              <div className="profile-info">
                <div className="info-group">
                  <label><FaUser /> Nickname</label>
                  <p>{userData?.nickname}</p>
                </div>

                <div className="info-group">
                  <label><FaEnvelope /> Email</label>
                  <p>{userData?.email}</p>
                </div>

                <button className="edit-button" onClick={handleEdit}>
                  Edit Profile
                </button>
              </div>
            )}

            <div className="password-section">
              <button 
                className="password-toggle"
                onClick={() => setShowPasswordSection(!showPasswordSection)}
              >
                <FaLock /> Change Password
              </button>

              {showPasswordSection && (
                <form onSubmit={handlePasswordSubmit} className="password-form">
                  {passwordError && (
                    <div className="error-message">
                      <p>{passwordError}</p>
                    </div>
                  )}
                  {passwordSuccess && (
                    <div className="success-message">
                      <p>{passwordSuccess}</p>
                    </div>
                  )}

                  <div className="form-group">
                    <label>Current Password</label>
                    <input
                      type="password"
                      name="currentPassword"
                      value={passwordData.currentPassword}
                      onChange={handlePasswordChange}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label>New Password</label>
                    <input
                      type="password"
                      name="newPassword"
                      value={passwordData.newPassword}
                      onChange={handlePasswordChange}
                      required
                      minLength="6"
                    />
                  </div>

                  <div className="form-group">
                    <label>Confirm New Password</label>
                    <input
                      type="password"
                      name="confirmPassword"
                      value={passwordData.confirmPassword}
                      onChange={handlePasswordChange}
                      required
                      minLength="6"
                    />
                  </div>

                  <button 
                    type="submit" 
                    disabled={passwordLoading}
                    className="change-password-button"
                  >
                    {passwordLoading ? 'Changing...' : 'Change Password'}
                  </button>
                </form>
              )}
            </div>
          </div>
        </div>
      </div>

      {selectedPost && (
        <div className="modal" onClick={() => setSelectedPost(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button className="modal-close" onClick={() => setSelectedPost(null)}>
              &times;
            </button>
            
            <div className="modal-header">
              <h2>{selectedPost.title}</h2>
              <span className="post-category">
                <FaTags />
                {selectedPost.category}
              </span>
            </div>

            <div className="modal-metadata">
              <div className="metadata-item">
                <FaUser /> Author: {selectedPost.author || '2025raj'}
              </div>
              <div className="metadata-item">
                <FaClock /> Posted: {formatDate(selectedPost.createdAt)}
              </div>
            </div>

            <div className="modal-body">
              <p>{selectedPost.content}</p>
            </div>

            <div className="modal-footer">
              <div className="post-stats">
                <span className="stat-item">
                  <FaHeart /> {selectedPost.likes} Likes
                </span>
                <span className="stat-item">
                  <FaComment /> {selectedPost.comments} Comments
                </span>
              </div>

              {selectedPost.tags && selectedPost.tags.length > 0 && (
                <div className="post-tags">
                  {selectedPost.tags.map((tag, index) => (
                    <span key={index} className="tag">#{tag}</span>
                  ))}
                </div>
              )}

              <div className="modal-actions">
                <button 
                  className="edit-button"
                  onClick={() => {
                    setSelectedPost(null);
                    navigate(`/edit-post/${selectedPost.id}`);
                  }}
                >
                  <FaEdit /> Edit Post
                </button>
                <button 
                  className="delete-button"
                  onClick={() => {
                    if (window.confirm('Are you sure you want to delete this post?')) {
                      handledele(selectedPost.id);
                      setSelectedPost(null);
                    }
                  }}
                >
                  <FaTrash /> Delete Post
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Profilepage;
