// src/pages/confession/CreateConfession.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/LoadingSpinner/LoadingSpinner';
import { createConfession } from '../../apis/Api';
import './CreateConfession.css';

const CreateConfession = () => {
    const navigate = useNavigate();
    const [content, setContent] = useState('');
    const [formData, setFormData] = useState({
        title: '',
        category: 'Personal',
        image: null,
        isAnonymous: false,
        author: '2025raj' // Current user's login
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [imagePreview, setImagePreview] = useState(null);
    const [characterCount, setCharacterCount] = useState(0);

    // Constants
    const MAX_TITLE_LENGTH = 100;
    const MAX_CONTENT_LENGTH = 5000;
    const MAX_IMAGE_SIZE = 5 * 1024 * 1024; // 5MB
    const ALLOWED_IMAGE_TYPES = ['image/jpeg', 'image/png', 'image/gif', 'image/webp'];

    const categories = [
        'Personal',
        'Relationship',
        'Work',
        'Family',
        'School',
        'Other'
    ];

    // Check authentication on mount
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login', { 
                state: { 
                    from: '/create-confession',
                    message: 'Please login to create a confession' 
                }
            });
        }
    }, [navigate]);

    // Form validation
    const validateForm = () => {
        if (!formData.title.trim()) {
            toast.error('Please enter a title for your confession');
            return false;
        }

        if (formData.title.length > MAX_TITLE_LENGTH) {
            toast.error(`Title must be less than ${MAX_TITLE_LENGTH} characters`);
            return false;
        }

        if (!content.trim()) {
            toast.error('Please write your confession');
            return false;
        }

        const plainTextContent = content.replace(/<[^>]*>/g, '');
        if (plainTextContent.length > MAX_CONTENT_LENGTH) {
            toast.error(`Confession must be less than ${MAX_CONTENT_LENGTH} characters`);
            return false;
        }

        return true;
    };

    // Input handlers
    const handleInputChange = (e) => {
        const { name, value, type, checked } = e.target;
        
        if (type === 'text' && name === 'title' && value.length > MAX_TITLE_LENGTH) {
            return;
        }

        setFormData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value
        }));
        setError('');
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (!file) return;

        // Validate file type
        if (!ALLOWED_IMAGE_TYPES.includes(file.type)) {
            toast.error('Please upload only JPEG, PNG, GIF, or WebP images');
            return;
        }

        // Validate file size
        if (file.size > MAX_IMAGE_SIZE) {
            toast.error('Image size should be less than 5MB');
            return;
        }

        setFormData(prev => ({ ...prev, image: file }));
        
        // Create image preview
        const reader = new FileReader();
        reader.onloadend = () => {
            setImagePreview(reader.result);
        };
        reader.onerror = () => {
            toast.error('Error reading image file');
        };
        reader.readAsDataURL(file);
    };

    const handleEditorChange = (event, editor) => {
        const data = editor.getData();
        setContent(data);
        const plainText = data.replace(/<[^>]*>/g, '');
        setCharacterCount(plainText.length);

        if (plainText.length > MAX_CONTENT_LENGTH) {
            setError(`Content exceeds maximum length of ${MAX_CONTENT_LENGTH} characters`);
        } else {
            setError('');
        }
    };

    // Form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) return;

        setLoading(true);
        setError('');

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('title', formData.title.trim());
            formDataToSend.append('content', content.trim());
            formDataToSend.append('category', formData.category);
            formDataToSend.append('isAnonymous', formData.isAnonymous);
            formDataToSend.append('createdAt', new Date().toISOString());
            
            if (formData.image) {
                formDataToSend.append('image', formData.image);
            }

            await createConfession(formDataToSend);
            toast.success('Confession shared successfully!');
            navigate('/confessions');
        } catch (err) {
            toast.error(err.message || 'Failed to create confession. Please try again.');
            setError(err.message || 'Failed to create confession. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    // Confirm before leaving with unsaved changes
    useEffect(() => {
        const handleBeforeUnload = (e) => {
            if (formData.title || content) {
                e.preventDefault();
                e.returnValue = '';
            }
        };

        window.addEventListener('beforeunload', handleBeforeUnload);
        return () => window.removeEventListener('beforeunload', handleBeforeUnload);
    }, [formData.title, content]);

    if (loading) {
        return <LoadingSpinner />;
    }

    return (
        <div className="create-confession-container">
            <h2>Share Your Confession</h2>
            {error && <div className="error-message">{error}</div>}
            
            <form onSubmit={handleSubmit} className="confession-form">
                <div className="form-group">
                    <label htmlFor="title">
                        Title 
                        <span className={`character-count ${
                            formData.title.length > MAX_TITLE_LENGTH * 0.9 ? 'near-limit' : ''
                        }`}>
                            {formData.title.length}/{MAX_TITLE_LENGTH}
                        </span>
                    </label>
                    <input
                        type="text"
                        id="title"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        required
                        maxLength={MAX_TITLE_LENGTH}
                        placeholder="Give your confession a title"
                        className="form-input"
                    />
                </div>

                <div className="form-group">
                    <label>
                        Your Confession
                        <span className={`character-count ${
                            characterCount > MAX_CONTENT_LENGTH * 0.9 ? 'near-limit' : ''
                        }`}>
                            {characterCount}/{MAX_CONTENT_LENGTH}
                        </span>
                    </label>
                    <CKEditor
                        editor={ClassicEditor}
                        data={content}
                        onChange={handleEditorChange}
                        config={{
                            toolbar: ['heading', '|', 'bold', 'italic', 'link', 'bulletedList', 'numberedList', '|', 'undo', 'redo'],
                            placeholder: 'Share your thoughts...',
                            maxLength: MAX_CONTENT_LENGTH
                        }}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="category">Category</label>
                    <select
                        id="category"
                        name="category"
                        value={formData.category}
                        onChange={handleInputChange}
                        required
                        className="form-select"
                    >
                        {categories.map(category => (
                            <option key={category} value={category}>
                                {category}
                            </option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label htmlFor="image">
                        Add Image (optional)
                        <span className="file-info">
                            Accepted formats: JPEG, PNG, GIF, WebP (Max: 5MB)
                        </span>
                    </label>
                    <input
                        type="file"
                        id="image"
                        name="image"
                        accept={ALLOWED_IMAGE_TYPES.join(',')}
                        onChange={handleImageChange}
                        className="form-input-file"
                    />
                    {imagePreview && (
                        <div className="image-preview">
                            <img src={imagePreview} alt="Preview" />
                            <button 
                                type="button" 
                                onClick={() => {
                                    setImagePreview(null);
                                    setFormData(prev => ({ ...prev, image: null }));
                                }}
                                className="remove-image-btn"
                            >
                                Remove Image
                            </button>
                        </div>
                    )}
                </div>

                <div className="form-group checkbox-group">
                    <label className="checkbox-label">
                        <input
                            type="checkbox"
                            name="isAnonymous"
                            checked={formData.isAnonymous}
                            onChange={handleInputChange}
                            className="checkbox-input"
                        />
                        Post Anonymously
                    </label>
                </div>

                <div className="form-group">
                    <button 
                        type="submit" 
                        className={`submit-button ${loading ? 'loading' : ''}`}
                        disabled={loading || !formData.title.trim() || !content.trim()}
                    >
                        {loading ? 'Posting...' : 'Share Confession'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default CreateConfession;
